import Pagination from '../P'

export default class View extends Component {
  constructor(props) {
    super(props);
    this.state = {
      returnList: [],
      searchString: '',
    }
  }

  componentDidMount() {
    this.props.setHeader("ReturnList");
    this.getReturnList();
  }

  getReturnList = () => {
    this.props.ajaxUtil.sendRequest(
      this.props.url_Inventory.RETURN_FILE_LIST_URL,
      {},
      (response) => {
        if (!response.status) {
          this.props.setNotification({
            message: response.message,
            hasError: response.status,
            timestamp: new Date().getTime()
          });
        } else {
          if (response.hasOwnProperty('data')) {
            this.setState({ returnList: response.data.content })
            console.log(response.data.content)
          }
        }
      },
      this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false }
    );
  }

  handleChange = (event) => {
    if (event.currentTarget.value.length <= 0) {
      this.getReturnList();
    }
    this.setState({ [event.currentTarget.name]: event.currentTarget.value });
  }
  onSearch = () => {
    const url = this.props.url_Inventory.RETURN_FILE_SEARCH_URL;
    this.props.ajaxUtil.sendRequest(
      url,
      [{
        "booleanValue": true,
        "fromDate": null,
        "key": "iccid",
        "toDate": null,
        "value": this.state.searchString
      }],
      (response) => {
        if (!response.status) {
          this.props.setNotification({
            message: response.message,
            hasError: response.status,
            timestamp: new Date().getTime()
          });
        } else {
          if (response.hasOwnProperty('data')) {
            this.setState({ returnList: response.data.content })
