import React, { Component } from 'react';
import ReactApexChart from 'react-apexcharts';
import moment from 'moment';
export default class AttendanceDetailsPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userAttendanceDetails: {},
      series: [0],
      options: {
        chart: {
          height: 170,
          type: 'radialBar',

        },
        plotOptions: {
          radialBar: {
            hollow: {
              size: '50%',
            }
          },
        },
        labels: ['Attendance'],
      },
    }
  }
  componentDidMount() {
    this.getUserDetails()
  }
  getUserDetails = () => {
    const url = this.props.url_training.GET_ATTENDANCE_DETAILS;
    this.props.ajaxUtil.sendRequest(url, {
      training_schedule_id: parseInt(this.props.scheduleId),
      user_id: parseInt(this.props.selectedUser)
    }, response => {
      if (!response.status) {
        this.props.setNotification({
          message: response.message,
          hasError: !response.status,
          timestamp: new Date().getTime()
        });
      }

      if (response.hasOwnProperty('data')) {
        this.setState({ userAttendanceDetails: response.data });
        this.setState({ series: [response.data.attendance] })
      }

    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });
  }
  render() {
    return (
      <div className="container-fluid">
        <div className="row mt-4">
          <div className="col-md-4 text-secondary">
            <div className="d-flex">
              <p>Participant Name:</p>
              <p className="details__right dark-grey">
                {this.state.userAttendanceDetails["firstName"]}&nbsp;{this.state.userAttendanceDetails["lastName"]}
              </p>
            </div>
            <div className="d-flex">
              <p>Gender:</p>
              <p className="details__right dark-grey">
                {this.state.userAttendanceDetails["gender"]}
              </p>
            </div>
            <div className="d-flex">
              <p>Email Id:</p>
              <p className="details__right dark-grey">
                {this.state.userAttendanceDetails["emailAddress"]}
              </p>
            </div>
            <div className="d-flex">
              <p>Contact:</p>
              <p className="details__right dark-grey"> {this.state.userAttendanceDetails["mobileNumber"]}</p>
            </div>
          </div>
          <div className="col-md-4 d-flex">
            <span className="attendence__date__range text-secondary">Date Range</span>
            {this.state.userAttendanceDetails['from_date']} {this.state.userAttendanceDetails['from_time']}&nbsp;-&nbsp;
            {this.state.userAttendanceDetails['to_date']} {this.state.userAttendanceDetails['to_time']}

          </div>
          <div className="col-md-4 mt--20">
            <div id="chart">
              <ReactApexChart options={this.state.options} series={this.state.series} type="radialBar" height={170} />
            </div>
          </div>
          <div className="col-md-12 mt-1 pt-2 mb-2 text-center border-top">
            <h6> {this.state.userAttendanceDetails['from_date']} &nbsp;-&nbsp;{this.state.userAttendanceDetails['to_date']} </h6>
          </div>
          <div className="col-md-12 mt-2">
            <div className="attendence__content">
              {this.state.userAttendanceDetails['attendanceDetails'] ? this.state.userAttendanceDetails['attendanceDetails'].map((details) => (
                <div className="data__day__wrap mb-30">
                  <label>{details.day}</label>
                  <div className="data__day__list">
                    <div className={(details.attendance_status == 'Present') ? "date__time" : 'date_time absent'}>
                      <h6>{moment(details.created_on).format("DD/MM/YYYY HH:MM")}</h6>
                    </div>
                    <div className="select__wrap">
                      <div className="custom-selectbox ">
                        <select id="Select1" className={(details.attendance_status == 'Present') ? "Select select-paceholder present h-100" : "Select absent select-paceholder h-100"}
                          name="options">
                          <option value={details.attendance_status}>{details.attendance_status}</option>
                        </select>
                      </div>
                    </div>
                  </div>
                </div>
              )) : (<div></div>)}
            </div>
          </div>
        </div>
      </div>
    )
  }
}
