import React, { Component, Fragment } from 'react';
import { Popup, POPUP_ALIGN } from '@6d-ui/popup';
import AddUserTraining from './AddUser';
import TrainingUserTableList from './TableList';

export default class UserList extends Component {

  constructor(props) {
    super(props);
    this.state = {
      addUserModal: false,
      userList: [],
    }
  }

  componentDidMount() {
    this.getUserList();
    this.props.setActive('user');

  }
  openUserModal = () => {
    this.setState({ addUserModal: true });
  }
  closeUserModal = () => {
    this.setState({ addUserModal: false });
  }
  changeStatus = (user, isDelete = false) => {
    let status = 2;
    if (!isDelete) {
      status = (user.status == "1") ? "0" : "1";
    }
    const data = {
      userId: user.id,
      status: status
    };
    this.props.ajaxUtil.sendRequest(this.props.url_training.CHANGE_USER_STATUS, data, response => {
      this.props.setNotification({
        message: response.message,
        hasError: !response.status,
        timestamp: new Date().getTime()
      });
      this.getUserList();
    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });
  }
  getUserList = () => {
    this.props.ajaxUtil.sendRequest(this.props.url_training.GET_USER_TRAINING_LIST, {}, response => {
      this.setState({ userList: response.data })
      if (!response.status) {
        this.props.setNotification({
          message: response.message,
          hasError: !response.status,
          timestamp: new Date().getTime()
        });
      }
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }
  render() {
    const { addUserModal } = this.state;
    const { openUserModal, closeUserModal } = this;
    const { url_training, ajaxUtil, loadingFunction, setNotification } = this.props;

    return (
      <Fragment>
        <div className="tab-pane">
          <div className="Sub__tab__header">
            <div className="sub__header__left">
              <h4>All Entries | {this.state.userList.length}</h4>
              <button className="add__training__btn" onClick={openUserModal} >Add User</button>
            </div>
            <div className="sub__header__right">
              <div className="searchbox ">
                <input type="search" placeholder="What are you looking for ?" />
                <button className="btn">
                  <i className="fa fa-search" aria-hidden="true"></i>
                </button>
              </div>
              <div className="filter">
                <img src="/dms/images/filter.svg" alt="filter" />
              </div>
            </div>

          </div>
          <div className="rejected__wrap">
            <div className="table-responsive">
              <table className="display dtable w-100">
                <thead>
                  <tr>
                    <th>S.NO</th>
                    <th>USERNAME</th>
                    <th>POSITION</th>
                    <th>STATUS</th>
                    <th>EDIT</th>
                    <th>DELETE</th>
                  </tr>
                </thead>
                <tbody>
                  <TrainingUserTableList
                    changeStatus={this.changeStatus}
                    users={this.state.userList} />

                </tbody>
              </table>
            </div>
          </div>
        </div>
        <Popup
          type={POPUP_ALIGN.RIGHT}
          title={"Add User"}
          isOpen={addUserModal}
          close={closeUserModal}
          minWidth="80%"
          component={
            <AddUserTraining
              closeUserModal={this.closeUserModal}
              getUserList={this.getUserList}
              url_training={url_training}
              ajaxUtil={ajaxUtil}
              loadingFunction={loadingFunction}
              setNotification={setNotification} />
          }
        />
      </Fragment>
    )
  }
}
