import React, { Component } from 'react';
export default class CreateRoleQuizModel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      description: '',
      maximumScore: '',
    }
  }

  addRole = (status) => {
    let error = false;
    Object.keys(this.state).map((key) => {
      if (this.state[key].trim().length <= 0) {
        if (key != 'description') {
          error = true;
        }
      }
    })
    if (error) {
      this.props.setNotification({
        message: 'Please Fill Up',
        hasError: true,
        timestamp: new Date().getTime()
      });
      return;
    }
    this.setState({ name: '' })
    this.setState({ description: '' })
    this.setState({ maximumScore: '' })
    this.props.addRoleData(this.state, status)


  }
  handleChange = (event) => {
    this.setState(({ [event.currentTarget.name]: event.currentTarget.value }))
  }
  resetForm = () => {
    this.setState({ name: '' })
    this.setState({ description: '' })
    this.setState({ maximumScore: '' })
  }
  render() {
    return (
      <div className="container-fluid">


        <div className="row" style={{ height: '85vh' }}>
          <div className="col-lg-12">
            <form action="">
              <div className="form-group">
                <label htmlFor="Training">Role Name</label>
                <input
                  name="name"
                  value={this.state.name}
                  onChange={(event) => this.handleChange(event)}
                  type="text"
                  className="form-control required" />
              </div>
              <div className="form-group mt-30">
                <label>Description</label>
                <textarea
                  name="description"
                  value={this.state.description}
                  onChange={(event) => this.handleChange(event)}
                  className="form-control"
                  rows="3">
                </textarea>
              </div>

              <div className="form-group">
                <label htmlFor="Material Name">Maximum Score</label>
                <input
                  type="number"
                  name="maximumScore"
                  value={this.state.maximumScore}
                  className="form-control required"
                  onChange={(event) => this.handleChange(event)} />
              </div>

              <div className="btn__create_quiz text-center">
                <button type="button" onClick={() => this.addRole(false)}><img src="/dms/images/plus.svg" alt="add" /> Add More</button>
              </div>
              {
                this.props.rolesList.map((role, index) => (
                  <div key={index} className="created__list__wrap mt-30">
                    <div className="list_item">

                      <div className="d-flex">
                        <h6>Role - {index + 1} - {role.name}</h6>
                      </div>
                      <button onClick={() => { if (window.confirm('Are you sure?')) { this.props.deleteRole(index) } }} className="list_delete"><i className="fa fa-trash"></i></button>
                    </div>
                  </div>
                ))
              }

            </form>

          </div>
          <div className="col-md-12 text-right " style={{ alignSelf: 'flexEnd' }}>
            <div className="button__work" style={{ alignSelf: 'flexEnd' }}>

              <button
                onClick={this.props.closeRoleModel}
                type="button"
                className="btn btn-danger cancel">Cancel</button>
              <button
                onClick={this.resetForm}
                type="button"
                className="btn btn-danger reset">Reset</button>
              <button
                onClick={() => this.addRole(true)}
                type="button"
                className="btn btn-danger add btn__save">Save</button>

            </div>
          </div>
        </div>
      </div>
    )
  }
}
