import React, { Component } from 'react'
import { Container, Row, Col, Nav, NavItem, NavLink, TabContent, TabPane } from 'reactstrap';
import { get, post } from 'axios'
import ListSyllabus from './ListSyllabus'
import ListModule from './ListModule';
import DashboardForm from './DashboardForm';
import MaterialList from './Modal/MaterialList'
import MaterialAdd from "./Modal/MaterialAdd";
import Loader from 'react-promise-loader';
import { trackPromise, usePromiseTracker } from 'react-promise-tracker';
import { ToastContainer, toast } from 'react-toastify';
import MaterialView from './Modal/MaterialView';
import { Link, Switch, Route } from 'react-router-dom';

const url_syllabus = CONSTANTS.SYLLABUS;

export default class index extends Component {
    constructor(props) {
        super(props)
        this.state = {
            posts: [],
            query: '',
            rowId: "",
            syllabusDetail: {},
            filterdPosts: [],
            openModal: false,
            isModuleListUpdated: false,
            showMaterialListModal: false,
            showMaterialViewModal: false,
            showAddMaterialModal: false,
            module_id: '',
            parent_subject: '',
            showAddSyllabusModal: false,
            material_id: '',
            approval_status:''
        }
    }
    componentDidMount() {
        this.fetchSyllabus()
    }

    fetchSyllabus(data=''){
        this.props.ajaxUtil.sendRequest(url_syllabus.LIST_URL + '?page=1&size=20&sort=subject_id&order=desc&type=Subject&keyword='+data,
            {},
            response => {
                    let responseData = response.data ? response.data : [];
                    this.setState({ posts: responseData, toalPost:response.totalElements, syllabusDetail: responseData[0] }, () => {})
            },  
            this.props.loadingFunction,
            { method: "GET", isShowSuccess: false });
    }

    loadDetail = (subject_id) => {
        const { posts } = this.state;
        const syllabusDetail = posts.filter((post) => post.subject_id === subject_id)[0];
        this.setState({ syllabusDetail, showMaterialListModal: false });
    }

    updatePosts = (data='') => {
       this.fetchSyllabus(data)
    }

    toggleModal = () => {
        this.setState({ openModal: true })
    }

    showMaterialList = (module_id) => {
        this.setState({ showMaterialListModal: true, module_id, isModuleListUpdated: false });
    }
    closeMaterialList = () => {
        this.setState({ showMaterialListModal: false });
    }
    closeMaterialShow = () => {
        this.setState({ showMaterialListModal: true, showMaterialViewModal:false  });

    }
    showAddSyllabusModal = () => {
        this.setState({ showAddSyllabusModal: true })
    }
    onOpenMaterialModal = (module_id) => {
        this.setState({ showAddMaterialModal: true, module_id, isModuleListUpdated: false })
    }
    onOpenMaterialViewModal = (material_id) => {
        this.setState({ showMaterialViewModal: true, showMaterialListModal:false, material_id })
    }
    cancelMaterialModal = (status) => {
        this.setState({ showAddMaterialModal: false, material_id: '' })
        if (status) {
            this.setState({ isModuleListUpdated: true })
        }
    }
    onEditMaterial = (material_id, approval_status='') => {
        this.setState({ showAddMaterialModal: true, material_id, isModuleListUpdated: false, approval_status })
    }

    render() {
        const { showAddSyllabusModal, syllabusDetail, toalPost,
            showAddMaterialModal,
            posts,
            showMaterialListModal,
            isModuleListUpdated,
            material_id, approval_status, showMaterialViewModal,
            module_id } = this.state;
        return (
            <div className="custom-container dashborad">
                  <Loader promiseTracker={usePromiseTracker} />
                <Container fluid>
                    <Row>
                        <Col>
                            <div>
                                <div className="container-fluid">
                                    <div className="row syllabus-details">
                                        <div className="col-md-12">
                                            {/* {!showMaterialListModal && !showMaterialViewModal && */}
                                            <div className="mainbg cus-pills">

                                                <ul className="nav nav-pills">
                                                    <li className="nav-item">
                                                        <a className="nav-link active" data-toggle="pill" href="#Details">
                                                            Details</a>
                                                    </li>
                                                    <li className="nav-item">
                                                        <a className="nav-link" data-toggle="pill" href="#Modules">
                                                            Modules</a>
                                                    </li>
                                                </ul>
                                                <div className="tab-content">

                                                    {/* <ListModule 
                                                        {...this.props}
                                                        parentSubject={syllabusDetail}
                                                        isModuleListUpdated={isModuleListUpdated}
                                                        showMaterialList={(module_id) => this.showMaterialList(module_id)}
                                                        onOpenMaterialModal={(module_id) => { this.onOpenMaterialModal(module_id) }}
                                                    /> */}

                                                </div>
                                            </div>



                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}
