import React, { Fragment } from 'react';
import PropTypes from 'prop-types';

export default function RenderSyllabusList(props) {
  const { syllabusList, selectedSyllabus, onSyllabusSelect } = props;
  let syllabusDiv = [];
  if (syllabusList.length <= 0) {
    syllabusDiv.push(
      <div className="IntroBox item-list"> No syllabus found </div>
    )
  } else {
    syllabusList.forEach((syllabus, index) => {
      syllabusDiv.push(
        <div 
          key={index} 
          onClick={() => onSyllabusSelect(syllabus)}
          className={(selectedSyllabus.subject_id == syllabus.subject_id) ? 'IntroBox item-list active-list' : 'IntroBox item-list '} >
          <a>
            <p className="name">{syllabus.subject_name}</p>
            <p className="desc">{syllabus.description}</p>
            <p className="created-date">{syllabus.created_date}</p>
          </a>
        </div>
      )
    })
  }
  return <Fragment>{syllabusDiv}</Fragment>;
}

RenderSyllabusList.prototype = {
  syllabusList: PropTypes.array,
  selectedSyllabus: PropTypes.object,
  onSyllabusSelect: PropTypes.func
}
