import React, { Component } from 'react';
import { Popup, POPUP_ALIGN } from '@6d-ui/popup';
import RoleAddModal from './RoleAddModal';
import RolePlayScoreAddModal from './RolePlayScoreAddModal';
import AttendanceDetailsPage from './AttendanceDetailsPage';
export default class AcceptedParticipantList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      roleAssignModal: false,
      rolePlayAssignModal: false,
      openAttendanceModal: false,
      selectedUser: null,
      selectedRole: null,
    }
  }

  openRoleAssignModal = (id) => {
    this.setState({ selectedUser: id, roleAssignModal: true })
  }
  closeRoleAssignModal = () => {
    this.setState({ selectedUser: null, roleAssignModal: false })
    this.props.getParticipantList();
  }
  openRolePlayAssignModal = (id, roleDetails) => {
    this.setState({ selectedRole: roleDetails[0]['rolePlayRoleId'] })
    this.setState({ selectedUser: id, rolePlayAssignModal: true })
  }
  closeRolePlayAssignModal = () => {
    this.setState({ selectedRole: null })
    this.setState({ selectedUser: null, rolePlayAssignModal: false })
    this.props.getParticipantList();
  }
  openAttendanceModal = (id) => {
    this.setState({ selectedUser: id, openAttendanceModal: true })
  }
  closeAttendanceModal = () => {
    this.setState({ selectedUser: null, openAttendanceModal: false })

  }
  render() {
    return (
      <div className="tab-pane show active " id="Accepted" role="tabpanel">
        <div className="trainig__name">Training: <strong>{decodeURI(this.props.trainingName)}</strong>
        </div>
        <div className="Sub__tab__header">
          <div className="sub__header__left">
            <h5>All Entries | {this.props.userList.length}</h5>
          </div>

          <div className="sub__header__right">
            <div className="searchbox ">
              <input type="search"
                placeholder="What are you looking for ?" />
              <button className="btn"><i className="fa fa-search"
                aria-hidden="true"></i></button>
            </div>
          </div>
        </div>
        <div className="table__wrap">
          <div className="table-responsive training__participant">
            <table className="display dtable w-100">
              <thead>
                <tr>
                  <th>S.No</th>
                  <th>Name</th>
                  <th>Role</th>
                  <th>Role play score</th>
                  <th>Quiz Score</th>
                  <th>Attendance</th>
                  <th>Details</th>
                </tr>
              </thead>
              <tbody>
                <RenderTableBody
                  openAttendanceModal={this.openAttendanceModal}
                  openRoleAssignModal={this.openRoleAssignModal}
                  openRolePlayAssignModal={this.openRolePlayAssignModal}
                  userList={this.props.userList} />
              </tbody>
            </table>
          </div>
        </div>
        <RenderModal
          closeAttendanceModal={this.closeAttendanceModal}
          openAttendanceModal={this.state.openAttendanceModal}
          selectedRole={this.state.selectedRole}
          selectedUser={this.state.selectedUser}
          scheduleId={this.props.scheduleId}
          ajaxUtil={this.props.ajaxUtil}
          loadingFunction={this.props.loadingFunction}
          setNotification={this.props.setNotification}
          url_training={this.props.url_training}
          roleAssignModal={this.state.roleAssignModal}
          rolePlayAssignModal={this.state.rolePlayAssignModal}
          closeRoleAssignModal={this.closeRoleAssignModal}
          closeRolePlayAssignModal={this.closeRolePlayAssignModal}
        />
      </div>
    )
  }
}

function RenderTableBody(props) {
  const getRolePlayDetails = (rolePlay) => {
    let rolePlays = rolePlay.map((role) => role.rolePlayName);
    return rolePlays.join(', ');
  }
  const getRolePlayDetailsScore = (rolePlay) => {
    let rolePlays = rolePlay.map((role) => role.rolePlayScore);
    return rolePlays.join(', ');
  }
  if (props.userList.length <= 0) {
    return (<tr>
      <td colSpan="7">No Data</td>
    </tr>)
  }
  let userList = props.userList.map((participant, index) => (
    <tr key={index}>
      <td>{index + 1}</td>
      <td>{participant.user_id}</td>
      <td>
        {participant.rolePlayDetails.length > 0 ? getRolePlayDetails(participant.rolePlayDetails) : (
          <button
            onClick={() => props.openRoleAssignModal(participant.user_id)}
            className="btn btn-action text_table_default">
            <img className="mr-2" src="/dms/images/plus.svg" alt="Assign" /> Assign
          </button>
        )}

      </td>
      <td>
        {getRolePlayDetailsScore(participant.rolePlayDetails)}
        &nbsp;
        {(participant.rolePlayDetails.length > 0) ? (
          <button
            onClick={() => props.openRolePlayAssignModal(participant.user_id, participant.rolePlayDetails)}
            className="btn btn-action text_table_default">
            <img className="mr-2" src="/dms/images/plus.svg" alt="Add" /> Add
          </button>
        ) : ''}

      </td>
      <td>{participant.score}</td>
      <td >
        <label
          onClick={() => props.openAttendanceModal(participant.user_id)}
          className="cursor-pointer">
          {(participant.attendance == "") ? 0 : participant.attendance}% attendance
        </label>
      </td>
      <td>
        <button className="btn btn-action">
          <span className="fa fa-eye light-grey"></span>
        </button>
      </td>
    </tr>
  ))
  return userList;
}

function RenderModal(props) {
  if (props.roleAssignModal) {
    return (
      <Popup
        type={POPUP_ALIGN.RIGHT}
        title="Assign Role"
        isOpen={true}
        close={props.closeRoleAssignModal}
        minWidth="50%"
        component={
          <RoleAddModal
            selectedUser={props.selectedUser}
            closeModal={props.closeRoleAssignModal}
            scheduleId={props.scheduleId}
            ajaxUtil={props.ajaxUtil}
            loadingFunction={props.loadingFunction}
            setNotification={props.setNotification}
            url_training={props.url_training}
          />
        }

      />
    )
  }
  if (props.openAttendanceModal) {
    return (
      <Popup
        type={POPUP_ALIGN.RIGHT}
        title="Attendance"
        isOpen={true}
        close={props.closeAttendanceModal}
        minWidth="90%"
        component={
          <AttendanceDetailsPage
            selectedUser={props.selectedUser}
            closeModal={props.closeRoleAssignModal}
            scheduleId={props.scheduleId}
            ajaxUtil={props.ajaxUtil}
            loadingFunction={props.loadingFunction}
            setNotification={props.setNotification}
            url_training={props.url_training}
          />
        }
      />
    )
  }
  if (props.rolePlayAssignModal) {
    return (
      <Popup
        type={POPUP_ALIGN.RIGHT}
        title="Role Play Score"
        isOpen={true}
        close={props.closeRolePlayAssignModal}
        minWidth="50%"
        component={
          <RolePlayScoreAddModal
            selectedRole={props.selectedRole}
            selectedUser={props.selectedUser}
            closeModal={props.closeRolePlayAssignModal}
            scheduleId={props.scheduleId}
            ajaxUtil={props.ajaxUtil}
            loadingFunction={props.loadingFunction}
            setNotification={props.setNotification}
            url_training={props.url_training}
          />
        }

      />
    )
  }

  return <></>
}
