import React, { useState } from 'react'
import { useTable, useSortBy, useExpanded, usePagination } from 'react-table'
import { Link } from 'react-router-dom';
function Table({ columns: userColumns, data }) {
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,

        prepareRow,
        page,
        canPreviousPage,
        canNextPage,
        pageOptions,
        pageCount,
        gotoPage,
        nextPage,
        previousPage,
        setPageSize,
        state: { pageIndex, pageSize },
    } = useTable(
        {
            columns: userColumns,
            data,
            initialState: { pageIndex: 0 },
        },
        useSortBy,
        usePagination // We can useExpanded to track the expanded state
        // for sub components too!
    )

    return (
        <div class="table-responsive">
            <table {...getTableProps()} className="data-table dataTable-mainTable table table-hover">
                <thead>
                    {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                                    {column.render('Header')}
                                    <span>
                                        {column.isSorted ? (!column.isSortedDesc ? ' ðŸ”¼' : ' ðŸ”½') : ''}
                                    </span>
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {page.map((row, i) => {
                        prepareRow(row)
                        return (
                            // Use a React.Fragment here so the table markup is still valid
                            <React.Fragment {...row.getRowProps()}>
                                <tr>
                                    {row.cells.map(cell => {
                                        return (
                                            <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                        )
                                    })}
                                </tr>
                            </React.Fragment>
                        )
                    })}
                </tbody>
            </table>
            <br />
            <div className="table__fixed__footer">
                <div className="footer__left">
                    <select className="custom-select"
                        value={pageSize}
                        onChange={e => {
                            setPageSize(Number(e.target.value))
                        }}
                    >
                        {[3, 5, 10].map(pageSize => (
                            <option key={pageSize} value={pageSize}>
                                {pageSize}
                            </option>
                        ))}
                    </select>
                    {/* <label>Records Per Page</label> */}
                    <span className='m-l-10'>Records Per Page</span>
                </div>
                <div className="footer__right">
                    <div className="page__enter">
                        <span>
                            Page{'   '}
                        </span>
                        <input
                            className="form-control"
                            type="number"
                            defaultValue={pageIndex + 1}
                            onChange={e => {
                                const page = e.target.value ? Number(e.target.value) - 1 : 0
                                gotoPage(page)
                            }}
                        />
                        <span>of {pageOptions.length}</span>
                    </div>


                    <nav aria-label="Page navigation">
                        <ul className="pagination">
                            <li className="page-item" onClick={() => gotoPage(0)} disabled={!canPreviousPage}><a className="page-link" href="#">First</a></li>
                            <li className="page-item" onClick={() => previousPage()} disabled={!canPreviousPage}><a className="page-link" href="#">Prev</a></li>
                            <li className="page-item" onClick={() => gotoPage(pageIndex + 1)}><a className="page-link" href="#">{pageIndex + 1}</a></li>
                            <li className="page-item" onClick={() => gotoPage(pageIndex + 1)}><a className="page-link" href="#">{pageIndex + 2}</a></li>
                            <li className="page-item" onClick={() => nextPage()} disabled={!canNextPage}><a className="page-link" href="#">Next</a></li>
                            <li className="page-item" onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}><a className="page-link" href="#">Last</a></li>
                        </ul>
                    </nav>
                </div>

            </div>
        </div>
    )
}
