import React, { Component } from 'react';
import TrainingListTable from './TrainingListTable';
import { Popup, POPUP_ALIGN } from '@6d-ui/popup';
import UploadResult from './uploadResult';
export default class TrainingList extends Component {

  constructor(props) {
    super(props);
    this.state = {
      scheduleList: [],
      selectedId: '',
      modalStatus: false,
    }
  }

  componentDidMount() {
    this.getScheduleList();
    this.props.setHeader("Training Management");

  }
  downloadResult = (id) => {
    const url = this.props.url_training.DOWNLOAD_RESULT;
    this.props.ajaxUtil.sendRequest(url, { training_id: id }, response => {
      if (response.status) {
        window.open(response.data, '_blank');
      }
      window.open(response.data, '_blank');


    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });
  }

  uploadResult = (id) => {
    this.setState({ selectedId: id });
    this.openModal();
  }
  openModal = () => {
    this.setState({ modalStatus: true })
  }
  closeModal = () => {
    this.setState({ modalStatus: false, selectedId: "" })
  }
  getScheduleList = () => {

    const url = this.props.url_training.LIST_TRAINING + '?page=1&size=1500&sort=id&order=desc&status=&keyword='
    this.props.ajaxUtil.sendRequest(url, {}, scheduleList => {
      console.log(scheduleList);
      if (scheduleList.hasOwnProperty('data')) {
        if (scheduleList.data != null && scheduleList.data.length > 0) {
          this.setState({ scheduleList: scheduleList.data });
        }
      }
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }

  render() {
    const { scheduleList } = this.state;
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <div className="mainbg cus-pills h-100">
              <ul className="nav nav-pills">
                <li className="nav-item">
                  <a className="nav-link active" href="">Training</a>
                </li>
              </ul>
              <div className="pzero  d-material-tabcontent">
                <div className="tab-pane active" id="Quiz">
                  <div className="Modules-txt h-100">
                    <div className="d-flex mb-10 modulesheader mt-30">
                      <div className="col-md-6 d-flex align-items-center">
                        <h4>All Entities | 01 </h4>
                        {/* <button className="add__training__btn">Schedule Training</button> */}
                      </div>
                      <div className="col-md-6 d-flex align-items-center justify-content-end">
                        <div className="searchbox">
                          <input type="search" placeholder="What are you looking for ?" />
                          <button className="btn"><i className="fa fa-search" aria-hidden="true" /></button>
                        </div>
                        <div className="filter">
                          <img src="/dms/images/filter.svg" alt="filter" />
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <TrainingListTable
                downloadResult={this.downloadResult}
                uploadResult={this.uploadResult}

                scheduleList={scheduleList} />
            </div>
          </div>
        </div>
        <Popup
          type={POPUP_ALIGN.RIGHT}
          title="Upload Result"
          isOpen={this.state.modalStatus}
          close={this.closeModal}
          minWidth="50%"
          component={
            <UploadResult id={this.state.selectedId} />
          }

        />
      </div>
    )
  }
}
