import React, { Fragment } from 'react';
import moment from 'moment';
import {Link} from 'react-router-dom';
export default function MaterialListTable(props) {
  const { materialList, onAddScheduleClick, materialStatusChange, openAddParticipantModel } = props;
  return (
    <div className="table-responsive">
      <table className="display dtable w-100" >
        <thead>
          <tr>
            <th>S.No</th>
            <th>MATERIALS</th>
            <th>Date Range</th>
            <th>PARTICIPANTS</th>
            <th>STATUS</th>
          </tr>
        </thead>
        <tbody>
          <TableBody
            onAddScheduleClick={onAddScheduleClick}
            materialList={materialList}
            materialStatusChange={materialStatusChange}
            openAddParticipantModel={openAddParticipantModel}
          />
        </tbody>
      </table>
    </div>
  )
}

function TableBody(props) {
  const { materialList, onAddScheduleClick, materialStatusChange, openAddParticipantModel } = props;
  console.log(materialList);
  let tableData = [];
  if (materialList.length > 0) {
    tableData = materialList.map((material, index) => (
      <tr key={index}>
        <td>{index + 1}</td>
        <td>{material.material_name}</td>
        <td>
          <ScheduleMaterial
            onAddScheduleClick={onAddScheduleClick}
            material={material} />
        </td>
        <td>
          <div className="d-flex justify-content-right align-items-center">
            {material.schedule['material_id'] ? <>
              <h6 className="p-1 mr-2 cursor_pointer">
                <Link to={`/Dashboard/MBookLet/Participant/${material.schedule['assessment_id']}`}>{material.schedule['participantcnt']}</Link>
              </h6>
              <img onClick={() => openAddParticipantModel(material)} className="cursor_pointer" src="/dms/images/plus.svg" alt="add button" />
              <span onClick={() => openAddParticipantModel(material)} className="pr-1 pl-1 cursor_pointer">Add Participant</span></> : ''}
          </div>
        </td>
        <td>

       { (material.hasOwnProperty('schedule') && !Array.isArray(material.schedule)) ?

     ( <div className="custom-control custom-switch cursor_pointer">
            <input onChange={(event) => materialStatusChange(event, material)} checked={(material.material_status == "1") ? true : false} type="checkbox" className="custom-control-input cursor_pointer" id="switch1" name="example" />
            <label className="custom-control-label cursor_pointer" htmlFor="switch1" />
          </div>) : <></>
    }
        </td>
      </tr>
    ))
    return tableData
  }
  return (
    <tr>
      <td colSpan="5">No Data</td>
    </tr>
  )
}


function ScheduleMaterial(props) {
  const { material, onAddScheduleClick } = props;
  if (material.hasOwnProperty('schedule')) {
    if (!Array.isArray(material.schedule)) {
      const startDateTime = moment(material.schedule.start_date + ' ' + material.schedule.start_time, 'YYYY-MM-DD hh:mm:ss').format('LLL')
      const endDateTime = moment(material.schedule.end_date + ' ' + material.schedule.end_time, 'YYYY-MM-DD hh:mm:ss').format('LLL')

      return <Fragment>{startDateTime}  to {endDateTime}</Fragment>
    }
  }
  return (
    <div onClick={() => onAddScheduleClick(material)} className="d-flex justify-content-start align-items-center cursor_pointer">
      <img src="/dms/images/plus.svg" alt="add button" className="mr-1" />
      <span className="pr-1 pl-1">Schedule Materials</span>
    </div>
  )
}
