import React, { Component } from 'react';

export default class FeedbackConfiguration extends Component {

  constructor(props) {
    super(props);
    this.state = {
      training_feedback: '',
      message: [""]
    }
  }
  componentDidMount() {
    this.props.setActive('feedback-configuration');
    this.getFeedBack();
  }
  getFeedBack = () => {

    let url = this.props.url_training.GET_FEEDBACK;
    this.props.ajaxUtil.sendRequest(url, {}, response => {
      console.log(response);
      if (response.hasOwnProperty('data')) {
        if (response['data'].hasOwnProperty('feedback')) {
          if (response['data']['feedback'].hasOwnProperty('training_feedback')) {
            this.setState({ training_feedback: response.data.feedback.training_feedback })
          }
        }
      }
      if(response.hasOwnProperty('data')) {
        if(response['data'].hasOwnProperty("feedback_msg")) {
          if(response["data"]["feedback_msg"].length > 0){
           let message =  response["data"]["feedback_msg"].map((message) => message.message);
           this.setState({message})
          }
        }
      }

    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }
  handleChange = (event) => {
    this.setState({ [event.currentTarget.name]: event.currentTarget.value })
  }
  addMessageField = () => {
    let message = [...this.state.message];
    message.push("");
    this.setState({ message });
  }
  deleteField = (index) => {
    let message = [...this.state.message];
    message.splice(index, 1);
    this.setState({ message });
  }
  updateValue = (event, index) => {
    let message = [...this.state.message];
    message[index] = event.currentTarget.value;
    this.setState({ message });
  }
  save = () => {
    let error = false;
    if (this.state.message.length <= 0 || this.state.training_feedback.length <= 0) {
      error = true;
    }
    if (error) {
      this.props.setNotification({
        message: 'please fil up',
        hasError: true,
        timestamp: new Date().getTime()
      });
      return;
    }
    let url = this.props.url_training.ADD_FEEDBACK;
    this.props.ajaxUtil.sendRequest(url, { ...this.state }, status => {
      this.props.setNotification({
        message: status.message,
        hasError: !status.status,
        timestamp: new Date().getTime()
      });
      this.getFeedBack();
    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });

  }
  render() {
    return (
      <div className="tab-pane">
        <div className="row">
          <div className="col-lg-5 mt-2">
            <div className="form__wraper ">
              <div className="feedback__headding mt-5">
                <h4>Training Feedback</h4>
              </div>
              <form>
                <div className="form-group mt-5">
                  <label>No of Times Feedback can be given</label>
                  <input
                    type="number"
                    value={this.state.training_feedback}
                    onChange={(event) => this.handleChange(event)}
                    className="form-control required Select"
                    name="training_feedback" />

                </div>
                <div className="feedback__headding mt-5 mb-5">
                  <h4>Factors</h4>
                </div>
                {this.state.message.map((message, index) => (
                  <div className="form-group mt-3" key={index}>
                    <div className="d-flex">
                      <input type="text" className="form-control" value={message} onChange={(event) => this.updateValue(event, index)} placeholder="Factor" />
                      <div className="d-flex">
                        <button type="button" className="btn" onClick={this.addMessageField}><img src="/dms/images/plus.svg" alt="add" /></button>
                        <button type="button" className="btn" onClick={() => this.deleteField(index)}><img src="/dms/images/minus.svg" alt="remove" /></button>
                      </div>
                    </div>
                  </div>
                ))}

                <div className="form-group mt-3">
                  <button type="button" onClick={this.save} className="add__training__btn">Save</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    )
  }

}
