import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import RejectedParticipantList from './userComponent/RejectedParticipantList';
import AcceptedParticipantList from './userComponent/AcceptedParticipantList';

export default class ParticipantList extends Component {

  constructor(props) {
    super(props);
    this.state = {
      participantListPagination: {},
      participantList: [],
    }
  }

  componentDidMount() {
    this.getParticipantList();
    this.props.setHeader("Training Management");
  }

  getParticipantList = () => {
    const url = this.props.url_training.GET_PARTICIPANT_LIST;
    this.props.ajaxUtil.sendRequest(url, {
      training_schedule_id: this.props.match.params.id
    }, participantList => {
      this.setState({ participantListPagination: participantList })
      if (participantList.hasOwnProperty('data')) {
        if (participantList.data != null && participantList.data.length > 0) {
          this.setState({ participantList: participantList.data });
        }
      }
    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });
  }
  render() {
    return (
      <div className="container-fluid">
        <div className="row mt-4">
          <div className="col-md-12">
            <div className="mainbg cus-pills h-100">
              <ul className="nav nav-pills">

                <li className="nav-item">
                  <a className="nav-link active" href="#Location">Participants</a>
                </li>
                <li className="nav-item _close ml-auto">
                  <Link className="nav-link " to="/Dashboard/Training/List">
                    <span
                      className="text-primary">&#10005;</span>
                  </Link>
                </li>
              </ul>
              <div className="tab-content p-0 d-material-tabcontent">
                <div className="tab-pane show active" id="Location">
                  <div className="sub-tab__wrap">
                    <ul className="nav nav-pills">
                      <li className="nav-item">
                        <a className="nav-link approved active" data-toggle="pill"
                          href="#Accepted">Accepted
                        </a>
                      </li>
                      <li className="nav-item">
                        <a className="nav-link rejected " data-toggle="pill"
                          href="#Rejected">Rejected</a>
                      </li>
                    </ul>
                    <div className="tab-content _participant">
                      <AcceptedParticipantList
                        getParticipantList={this.getParticipantList}
                        userList={this.state.participantList}
                        trainingName={this.props.match.params.name}
                        scheduleId={this.props.match.params.id}
                        ajaxUtil={this.props.ajaxUtil}
                        loadingFunction={this.props.loadingFunction}
                        setNotification={this.props.setNotification}
                        url_training={this.props.url_training}
                      />
                      <RejectedParticipantList
                        trainingName={this.props.match.params.name}
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
