import React, { Component } from 'react';
import { Multiselect } from 'multiselect-react-dropdown';
import { Popup, POPUP_ALIGN } from '@6d-ui/popup';
import AddQuizModal from './AddQuizModal';
import CreateRoleQuizModel from './CreateRoleQuizModel';
function RenderOptions(props) {
  const { data, keyValue, value } = props;
  if (data.length > 0) {
    let options = data.map((option, index) => (
      <option key={option[value] + index} value={option[keyValue]}> {option[value]} </option>
    ))
    return [<option key="-10" defaultValue value="" disabled> Please Select</option>, ...options];
  }
  return (
    <option value=''>Please Select</option>
  )
}
export default class CreateQuiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isEdit: false,
      materialList: [],
      syllabusList: [],
      moduleList: [],
      moduleViewList: [],
      oldRoleList: [],
      selectedQuiz: {
        syllabus: '',
        module: '',
        material: [],
        passing_score: {},
        quiz_list: {},
        rolePlayName: '',
      },
      selectedMaterialId: '',
      addQuizModal: false,
      addRoleModel: false,
      rolesList: [],



    }
  }

  componentDidMount() {
    this.checkIsEdit();
    this.getSyllabus();
    this.getModule();
  }

  checkIsEdit = () => {
    if (this.props.match.params.hasOwnProperty("edit") && this.props.match.params.edit !== undefined ) {
      const url = this.props.url_training.GET_MATERIAL_DETAILS_BY_SCHEDULE_ID;
      this.props.ajaxUtil.sendRequest(url, {
        trainingId: this.props.match.params.schedule_id
      }, response => {
        this.getMaterialistByModule(response.data.materials[0]['moduleId']);
        const data = {
          syllabus: response.data.materials[0]['syllabusId'],
          module: response.data.materials[0]['moduleId'],
          material: [...response.data.materials[0]['material_id']],
          passing_score: {},
          quiz_list: {[response.data.materials[0]['material_id']]:response.data.materials[0]['quizId']},
          rolePlayName: response.data.role_play[0]['role_play_title']
        }
        const roleList = [];
        response.data.role_play.forEach((roles) => {
          if (roles.roleDetails.length > 0) {
            roles.roleDetails.forEach((data) => {
              roleList.push({
                id: data.id,
                role: data.role
              })
            })

          }
        })
        this.setState({ oldRoleList: roleList })
        this.setState({ selectedQuiz: data })
      }, this.props.loadingFunction,
        { method: 'POST', isShowSuccess: false });
      this.setState({ isEdit: true })
    } else {
      this.setState({ isEdit: false })
    }
  }
  getSyllabus = () => {
    const url = this.props.url_training.SYLLABUS_LIST
    this.props.ajaxUtil.sendRequest(url, {}, syllabusList => {
      if (syllabusList.hasOwnProperty('data')) {
        if (syllabusList.data != null && syllabusList.data.length > 0) {
          this.setState({ syllabusList: syllabusList.data });
        }
      }
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }

  getModule = () => {
    const url = this.props.url_training.MODULE_ID
    this.props.ajaxUtil.sendRequest(url, {}, moduleList => {
      if (moduleList.hasOwnProperty('data')) {
        if (moduleList.data != null && moduleList.data.length > 0) {
          this.setState({ moduleList: moduleList.data });
          if (this.state.isEdit) {
            const modules = this.state.moduleList.filter((module) => module.parent_subject_id == this.state.selectedQuiz.syllabus);
            this.setState({ moduleViewList: modules });
          }
        }
      }
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }

  handleChange = (event) => {
    let data = { ...this.state.selectedQuiz };
    const name = event.currentTarget.name;
    const value = event.currentTarget.value;

    switch (name) {
      case 'syllabus':
        const modules = this.state.moduleList.filter((module) => module.parent_subject_id == value);
        if (modules.length <= 0) {
          this.props.setNotification({
            message: 'No Modules Found',
            hasError: true,
            timestamp: new Date().getTime()
          });
        }
        this.setState({ moduleViewList: modules });
        this.setState({ materialList: [] })
        data['module'] = '';
        data['material'] = [];
        data['passing_score'] = {};

        break;
      case 'module':
        this.getMaterialistByModule(value);
        data['material'] = [];
        data['passing_score'] = {};
      default:
        break;
    }
    data[name] = value;
    this.setState({ selectedQuiz: data })

  }
  getMaterialistByModule = (id) => {
    const url = this.props.url_training.MATERIAL_LIST + '?id=' + id
    this.props.ajaxUtil.sendRequest(url, {}, materialList => {
      if (materialList.statusCode == 400) {
        this.props.setNotification({
          message: 'No Material Found',
          hasError: true,
          timestamp: new Date().getTime()
        });
      }

      if (materialList.hasOwnProperty('data')) {
        if (materialList.data != null && materialList.data.length > 0) {
          this.setState({ materialList: materialList.data });
        }
      }
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }

  onSelect = (selectedList, selectedItem) => {
    const data = { ...this.state.selectedQuiz };
    data['material'] = [...selectedList];
    this.setState({ selectedQuiz: data });
  }

  onRemove = (selectedList, removedItem) => {
    const data = { ...this.state.selectedQuiz };
    data['material'] = [...selectedList];
    this.setState({ selectedQuiz: data });
  }
  setPassingScore = (event, material) => {
    const data = { ...this.state.selectedQuiz };
    data.passing_score[material] = event.currentTarget.value;
    this.setState({ selectedQuiz: data });
  }
  generateNumber = (limit) => {
    let numbers = [];
    for (let index = 0; index <= limit; index++) {
      numbers.push({ id: index, value: index })
    }
    return numbers;
  }
  openQuizModel = (materialId) => {
    this.setState({ selectedMaterialId: materialId });
    this.setState({ addQuizModal: true })
  }
  closeAddQuizModal = () => {
    this.setState({ addQuizModal: false })
  }
  openRoleModel = () => {
    this.setState({ addRoleModel: true })
  }
  createQuizs = (data) => {
    let quiz = {
      material_id: this.state.selectedMaterialId,
      training_type: "1",
      type: "training",
      questionCount: data.questionNumber,
      start_date: "",
      start_time: "",
      time: "",
      end_date: "",
      end_time: "",
      questions: data.selectedQuestions.map((question) => question.question_id)

    }
    const url = this.props.url_training.CREATE_QUIZ;
    this.props.ajaxUtil.sendRequest(url, quiz, quizData => {
      console.log(quizData)
      this.props.setNotification({
        message: quizData.message,
        hasError: (quizData.statusCode == 400),
        timestamp: new Date().getTime()
      });
      if (quizData.statusCode == 200) {
        const s = { ...this.state.selectedQuiz };
        s.quiz_list[this.state.selectedMaterialId] = { id: quizData.st, number: data.questionNumber };
        this.setState({ selectedQuiz: s });
      }

    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });
    this.closeAddQuizModal();
  }

  closeRoleModel = () => {
    this.setState({ addRoleModel: false })
  }

  addRoleData = (data, closeModel = false) => {
    let roles = [...this.state.rolesList];
    roles.push(data);
    this.setState({ rolesList: roles })
    if (closeModel) {
      this.closeRoleModel();
    }
  }
  deleteRole = (index) => {
    let roles = [...this.state.rolesList];
    roles.splice(index, 1);
    this.setState({ rolesList: roles })
  }
  deleteOldRole = (id, index) => {
    const url = this.props.url_training.DELETE_ROLE_PLAY;
    this.props.ajaxUtil.sendRequest(url, {
      "training_schedule_id": this.props.match.params.schedule_id,
      "role_play_id": parseInt(id)
    }, data => {
      if (data.status) {
        let roles = [...this.state.oldRoleList];
        roles.splice(index, 1);
        this.setState({ oldRoleList: roles })
      }
      this.props.setNotification({
        message: data.message,
        hasError: !data.status,
        timestamp: new Date().getTime()
      });
    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });
  }
  deleteMaterial = (index) => {
    let data = { ...this.state.selectedQuiz };
    const materialList = [...this.state.materialList]
    if (data.material.length > 1) {
      data.material.splice(index, 1);
    } else {
      data.material = [];
    }
    this.setState({ selectedQuiz: data, materialList: materialList })

  }
  createQuiz = () => {
    let error = false;
    if (this.state.selectedQuiz.syllabus.length <= 0 ||
      this.state.selectedQuiz.module.length <= 0 ||
      // this.state.selectedQuiz.rolePlayName.length <= 0 ||
      this.state.selectedQuiz.material.length <= 0
      // Object.keys(this.state.selectedQuiz.passing_score).length <= 0||
      // Object.keys(this.state.selectedQuiz.quiz_list).length <= 0
    ) {
      error = true;
    }

    if (error) {
      this.props.setNotification({
        message: 'Please Fill Up',
        hasError: true,
        timestamp: new Date().getTime()
      });
      return;
    }
    let passing_score = this.state.selectedQuiz.material.map((material) => this.state.selectedQuiz.passing_score[material.material_id])
    let quiz_list = []; this.state.selectedQuiz.material.map((material) => {
      if (this.state.selectedQuiz.quiz_list.hasOwnProperty(material.material_id)) {
        if (this.state.selectedQuiz.quiz_list[material.material_id].hasOwnProperty('id')) {
          quiz_list.push(this.state.selectedQuiz.quiz_list[material.material_id]['id'])
        }
      }

    })


    let data = {
      training_id: this.props.match.params.schedule_id,
      materialIds: this.state.selectedQuiz.material.map((material) => material.material_id),
      role_play: this.state.selectedQuiz.rolePlayName,
      pass_score: ['1'],
      quiz: (quiz_list.length < 0) ? ["1"] : quiz_list,
      role_name: this.state.rolesList.map((role) => role.name),
      role_score: this.state.rolesList.map((role) => role.maximumScore),
      role_description: this.state.rolesList.map((role) => role.description),
    }
    let url = this.props.url_training.SCHEDULE_QUIZ
    if (this.state.isEdit) {
      url = this.props.url_training.UPDATE_QUIZ
    }
    this.props.ajaxUtil.sendRequest(url, data, response => {
      this.props.setNotification({
        message: response.message,
        hasError: !response.status,
        timestamp: new Date().getTime()
      });
      if (response.status) {
        if(this.state.isEdit){
          this.props.history.push('/Dashboard/Schedule/Training/QuestionSet/' + this.props.match.params.schedule_id + '/' + data.materialIds.join("-")+'/edit')
        }else {
          this.props.history.push('/Dashboard/Schedule/Training/QuestionSet/' + this.props.match.params.schedule_id + '/' + data.materialIds.join("-"))
        }
      }
    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });

