import React, { useState } from 'react';
import PropTypes from 'prop-types'
import { useTable,   usePagination } from 'react-table'




const Table = ({ cols, data}) => {
    const {
        pageSize,
        canPreviousPage,
        canNextPage,
        pageOptions,
        pageCount,
        gotoPage,
        nextPage,
        previousPage,
        setPageSize }= useState('');

    return (


        <div class="table-responsive">
            <table className="data-table dataTable-mainTable table table-hover">
                <thead>
                    <tr>
                        {cols.map((headerItem, index) => (
                            <th key={index}>{headerItem.title}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {data.map((item, index) => (
                        <tr key={index}>
                            {cols.map((col, key) => (
                                <td key={key}>{col.render(item)}</td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>

            <br />
            <br />
            <div className="col-md-12 pagination grid-pagination">
                <div className="col-md-4">
                    <div className="form-group">
                        <select className="form-control"
                            value={pageSize}
                            onChange={e => {
                                setPageSize(Number(e.target.value))
                            }}
                        >
                            {[10, 20, 30, 40, 50].map(pageSize => (
                                <option key={pageSize} value={pageSize}>
                                    {pageSize}
                                </option>
                            ))}
                        </select>
                        <span className='m-l-10'>Records Per Page</span>
                    </div>
                </div>
                <div className="col-md-3 form-group">
                    <div className="form-group">
                        <span>
                            Page{' '}
                        </span>
                        <input
                            className="form-control m-l-10 "
                            type="number"
                            defaultValue={pageIndex + 1}
                            onChange={e => {
                                const page = e.target.value ? Number(e.target.value) - 1 : 0
                                gotoPage(page)
                            }}
                        />
                        <span className="m-l-10">of {pageOptions.length}</span>
                    </div>
                </div>
                <div className="col-md-5">
                    <ul>
                        <li onClick={() => gotoPage(0)} disabled={!canPreviousPage}><a href="#">First</a></li>
                        <li onClick={() => previousPage()} disabled={!canPreviousPage}><a href="#">Prev</a></li>
                        <li onClick={() => gotoPage(pageIndex + 1)}><a href="#">{pageIndex + 1}</a></li>
                        <li onClick={() => gotoPage(pageIndex + 1)}><a href="#">{pageIndex + 2}</a></li>
                        <li onClick={() => nextPage()} disabled={!canNextPage}><a href="#">Next</a></li>
                        <li onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}><a href="#">Last</a></li>
                    </ul>

                </div>
            </div>
        </div>
    )
}

Table.propTypes = {
    cols: PropTypes.array.isRequired,
    data: PropTypes.array.isRequired
}



export default Table;
