import React, { Component } from 'react'
import { Container, Row, Col, Nav, NavItem, NavLink, TabContent, TabPane } from 'reactstrap';
import _ from 'lodash';
import { get, post } from 'axios'
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
import { CONSTANTS } from '../../util/Constants'
// import ListSyllabus from './ListSyllabus'
import { ToastContainer, toast } from 'react-toastify';
import SyllabusAdd from './Modal/SyllabusAdd';
var moment = require('moment');

const url_syllabus = CONSTANTS.SYLLABUS;
const regex = /[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;

export default class DetailTab extends Component {

  constructor(props) {
    super(props)
    this.state = {
      posts: [],
      subject_name: '',
      subject_type_id: 'Subject',
      parent_subject: '',
      rowId: "",
      subject_id: "",
      parent_subject_id: "",
      description: "",
      type: "",
      isModalDismiss: false,
      showAddSyllabusModal: this.props.showAddSyllabusModal,
    }
    this.changeHandler = this.changeHandler.bind(this)
    this.updateHandler = this.updateHandler.bind(this)
  }

  // componentDidUpdate(prevProps) {
  //   if (prevProps.showAddSyllabusModal !== this.props.showAddSyllabusModal || ) {
  //     this.setState({ showAddSyllabusModal: true })
  //   }
  // }

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value })
  }

  formValidate = () => {
    if (regex.test(this.state.subject_name)) {
      toast.error('Syllabus name should not contain any special characters');
      return;
    }
    if (this.state.subject_name.trim() == "") {
      toast.error('Please enter Syllabus name');
      return;
    }
    if (this.state.type.trim() == "") {
      toast.error('Please select syllabus type');
      return;
    }
    return true;
  }

  reset = () => {
    this.setState({
      subject_name: '', parent_subject: '', description: '', type: ''
    })
  }

  deleteSyllabus(id) {
    const deleteId = {
      subject_id: id
    }
    post(url_syllabus.DELETE_URL, deleteId)
      .then((response) => {
        const res = response.data;
        if (res.statusCode == 200) {
          toast.success('Success: ' + res.message);
          this.props.updatePosts()
          this.setState({ isModalDismiss: true })
        } else {
          toast.error('Error: ' + res.message);
        }
      }, (error) => {
        // console.log(error);
      });
  }

  deleteClick(id, message) {
    this.props.setModalPopup({
      'rowId': id,
      'isOpen': true,
      'onConfirmCallBack': () => this.deleteSyllabus(id),
      'title': "Confirm Delete",
      'content': "Are you sure to Delete " + message + '?',
      'CancelBtnLabel': "Cancel",
      'confirmBtnLabel': "Delete"
    });
  }

  setRowId(rowId) {

    post(url_syllabus.EDIT_URL, { subject_id: rowId })
      .then((response) => {
        let syllabusDetails = response.data.data;
        this.setState({
          subject_id: syllabusDetails.subject_id,
          subject_name: syllabusDetails.subject_name,
          description: syllabusDetails.description,
          type: syllabusDetails.class_id
        })
      }, (error) => {
        console.log(error);
      });
  }

  updateHandler = (e) => {
    e.preventDefault();
    const data = {
      subject_id: this.state.subject_id,
      subject_name: this.state.subject_name,
      subject_type_id: this.state.subject_type_id,
      parent_subject: this.state.parent_subject,
      description: this.state.description,
      type: this.state.type
    };
    const isFormValid = this.formValidate();
    if (isFormValid) {
      post(url_syllabus.UPDATE_URL, data)
        .then((response) => {
          const res = response.data;
          if (res.statusCode == 200) {
            this.props.updatePosts();
            this.reset();
            toast.success('Success: ' + res.message);
          } else {
            toast.error('Error: ' + res.message);
          }
        }, (error) => {
          toast.error('Failure')
        });
    }
    this.cancelButton.click()
  }
