import React, { Component, useState, useEffect } from "react";
import { Col, Row, Container, Input, Label, ModalBody, Modal, ModalHeader, ModalFooter } from "reactstrap";
import _ from "lodash";
import { Switch } from '@6d-ui/form';
import { FieldItem, FIELD_TYPES } from '@6d-ui/fields';

const Details = (props) => {

  const buybackData = props;
  console.log(buybackData);

  const [height, setHeight] = useState(window.innerHeight);
  const [viewData, setViewData] = useState({});
  const [modal, setModal] = useState(true);

  useEffect(() => {

    window.addEventListener("resize", () => setHeight(window.innerHeight - 68));

  }, []);

  useEffect(() => {
    if (buybackData) {
      setViewData(buybackData.data);
    }

    console.log("data", viewData);
  })
  // const toggle = () => setModal(false);

  return (
    <div>
         <Modal isOpen={modal} modalTransition={{ timeout: 700 }} backdropTransition={{ timeout: 1300 }}>
        <div
          className="custom-container overlay_position scrollbar"
          style={{ 'height': height }}
        >

          <div className="form-Brick">
              <div className="form-Brick-Head">
                <span>Detailed View  {viewData.fgCode}</span>
              </div>
              <div className="form-Brick-body">
                <Row className="mx-0">
                  <Container>
                    <Row className="form-data">
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            Fg Code
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.fgCode}
                          </Col>
                        </Row>
                      </Col>
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            Status
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.status}
                          </Col>
                        </Row>
                      </Col>
                    </Row>
                  </Container>
                </Row>
              </div>


              <div className="form-Brick-body">
                <Row className="mx-0">
                  <Container>
                    <Row className="form-data">
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            Buyback Type
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.buyBackType}
                          </Col>
                        </Row>
                      </Col>
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            Channel
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.channel}
                          </Col>
                        </Row>
                      </Col>
                    </Row>
                  </Container>
                </Row>
              </div>


              <div className="form-Brick-body">
                <Row className="mx-0">
                  <Container>
                    <Row className="form-data">
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            Distributer
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.distributer}
                          </Col>
                        </Row>
                      </Col>
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            Region
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.region}
                          </Col>
                        </Row>
                      </Col>
                    </Row>
                  </Container>
                </Row>
              </div>

              <div className="form-Brick-body">
                <Row className="mx-0">
                  <Container>
                    <Row className="form-data">
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            Outlet
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.outlet}
                          </Col>
                        </Row>
                      </Col>
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            Cluster
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.cluster}
                          </Col>
                        </Row>
                      </Col>
                    </Row>
                  </Container>
                </Row>
              </div>

              <div className="form-Brick-body">
                <Row className="mx-0">
                  <Container>
                    <Row className="form-data">
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            validity range
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.fromDate} - {viewData.toDate}
                          </Col>
                        </Row>
                      </Col>
                      <Col xs="6">
                        <Row>
                          <Col className="form-data-label" xs="6">
                            Sell True Check
                        </Col>
                          <Col className="form-data-value" xs="6">
                            {viewData.sellThruCheck ? "Yes" : "No"}
                          </Col>
                        </Row>
                      </Col>
                    </Row>
                  </Container>
                </Row>
              </div>
          </div>
          </div>
        </Modal>
 
    </div>

  )
}

export default Details;
