import React, { useEffect } from 'react';
import {
  CustomButton,
  BUTTON_STYLE,
  BUTTON_TYPE,
  BUTTON_SIZE,
  COLOR
} from '@6d-ui/buttons';
import moment from 'moment';
import daterangepicker, { dateToday } from 'daterangepicker';
import $ from 'jquery';
export default function AddScheduleToMaterial(props) {
  const datePicker = React.createRef();
  useEffect(() => {
    if (datePicker.current) {
      $('#datePicker').daterangepicker({
        timePicker: true,
        autoUpdateInput: true,
        minDate: '0',
        locale: {
          format: 'DD/MM/YYYY hh:mm A'
        }
      }, function (start, end, label) {
        updateScheduleDateRange(start, end)
      });
      datePicker.current.click();
    }
  }, [])

  const { dateValue, closeAddScheduleModal, updateScheduleDateRange, onCreateSchedule, onAccessChange } = props;
  return (
    <div className="container-fluid h-100 d-flex flex-column justify-content-between">
      <div>
        <div className="form-group mt-2">
          <label htmlFor="Date Picker Range">Date Picker Range</label>
          <input id="datePicker"  ref={datePicker}type="text" className="form-control scheduleMaterial-daterange daterange" placeholder="Date Range" />
        </div>
        <div className="form-group mt-2">
          <label htmlFor="Date Picker Range">Access</label>
          <select name="access" onChange={(event) => onAccessChange(event)} className="form-control " >
            <option value="">Please Select</option>
            <option value="Read">Read</option>
            <option value="Download">Download</option>
          </select>
        </div>
      </div>
      <div className="mb-2">
        <CustomButton
          style={BUTTON_STYLE.BRICK}
          type={BUTTON_TYPE.PRIMARY}
          size={BUTTON_SIZE.LARGE}
          align="right"
          label="Create"
          isButtonGroup={true}
          onClick={onCreateSchedule}
        />
        <CustomButton
          style={BUTTON_STYLE.BRICK}
          type={BUTTON_TYPE.SECONDARY}
          size={BUTTON_SIZE.LARGE}
          color={COLOR.PRIMARY}
          align="right"
          label="Reset"
          isButtonGroup={true}
          onClick={() => { updateScheduleDateRange(null, null); $('#datePicker').val(''); }}
        />
        <button type="button" className="btn btn-danger cancel " onClick={closeAddScheduleModal}>Cancel</button>
      </div>
    </div>
  )
}
