import React, { Component, Fragment } from 'react';

export default class AddOrEditGame extends Component {

  constructor(props) {
    super(props);
    this.state = {
      optionList: [{ option: '', order: '' }],
      gameTypes: [],
      gameType: '',
      gameName: '',
      challenges: '',
      clue: '',
      minutes: '',
      originalFile: '',
      imageOptions: [{ file: '', order: '' }],
      pushGame: [{ file: '', order: '', option: '' }]
    };
  }
  componentDidMount() {
    this.getGameTypes();
  }
  getGameTypes = () => {
    const url = this.props.url_game.GET_GAME_TYPES
    this.props.ajaxUtil.sendRequest(url, {}, gameTypes => {
      if (!gameTypes.status) {
        this.props.setNotification({
          message: gameTypes.message,
          hasError: !gameTypes.status,
          timestamp: new Date().getTime()
        });
      }
      this.setState({ gameTypes: gameTypes.data });
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }
  originalImageChange = (event) => {
    this.setState({ originalFile: event.target.files[0] })
  }
  handleImagePartChange = (event, index) => {
    let imageOptions = [...this.state.imageOptions];
    if (event.currentTarget.name == 'file') {
      imageOptions[index]['file'] = event.target.files[0]
    }
    if (event.currentTarget.name == 'order') {
      imageOptions[index]['order'] = event.target.value
    }
    this.setState({ imageOptions });

  }
  deletedImageOption = (index) => {
    let imageOptions = [...this.state.imageOptions];
    if (imageOptions.length != 1) {
      imageOptions.splice(index, 1);
      this.setState({ imageOptions });
    }
  }

  addImageOption = () => {
    let imageOptions = [...this.state.imageOptions];
    imageOptions.push({ file: '', order: '' })
    this.setState({ imageOptions });
  }
  handleChange = (event) => {
    const value = event.currentTarget.value;
    const name = event.currentTarget.name;
    switch (name) {
      case 'gameType':
        this.setState({ gameType: value });
        break;

      default:
        this.setState({ [name]: value });
        break;
    }
  }
  optionChange = (event, index) => {
    let options = [...this.state.optionList];
    options[index][event.currentTarget.name] = event.currentTarget.value;
    this.setState({ optionList: options });
  }
  addOption = () => {
    let options = [...this.state.optionList];
    options.push({ option: '', order: '' });
    this.setState({ optionList: options });
  }
  deletedOption = (index) => {
    let options = [...this.state.optionList];
    if (options.length != 1) {
      options.splice(index, 1);
      this.setState({ optionList: options });
    }
  }
  handlePushGameChange = (event, index) => {
    let imageOptions = [...this.state.pushGame]
    if (event.currentTarget.name == 'file') {
      imageOptions[index]['file'] = event.target.files[0]
    } else {
      imageOptions[index][event.currentTarget.name] = event.currentTarget.value;
    }
    this.setState({ pushGame: imageOptions })
  }
  addPushGame = () => {
    let options = [...this.state.pushGame];
    options.push({ option: '', order: '', file: '' });
    this.setState({ pushGame: options });
  }
  deletedPushGame = (index) => {
    let options = [...this.state.pushGame];
    if (options.length != 1) {
      options.splice(index, 1);
      this.setState({ pushGame: options });
    }
  }
  save = () => {
    if (this.state.gameType.length <= 0 || this.state.minutes.length <= 0 || this.state.clue.length <= 0 || this.state.challenges.length <= 0 || this.state.gameName.length <= 0) {
      this.props.setNotification({
        message: 'Please Fill Up',
        hasError: true,
        timestamp: new Date().getTime()
      });
      return;
    }
    const formData = new FormData();
    formData.append('game_type_id', this.state.gameType);
    formData.append('game_name', this.state.gameName);
    formData.append('game_challenge', this.state.challenges);
    formData.append('game_clue', this.state.clue);
    formData.append('game_duration', (parseInt(this.state.minutes) * 60));

    switch (this.state.gameType) {
      case '1':
      case '3':
        if (this.state.originalFile == "") {
          this.props.setNotification({
            message: 'Please Fill Up',
            hasError: true,
            timestamp: new Date().getTime()
          });
          return;
        }
        let status = false;
        this.state.imageOptions.forEach((option) => {
          if (option.file == '' || option.order == '') {
            status = true;
          }
        });
        if (status) {
          this.props.setNotification({
            message: 'Please Fill Up',
            hasError: true,
            timestamp: new Date().getTime()
          });
          return;
        }
        formData.append('original_img', this.state.originalFile);
        this.state.imageOptions.forEach((option) => {
          formData.append('puzzle_img_path[]', option.file);
        })
        this.state.imageOptions.forEach((option) => {
          formData.append('puzzle_order[]', parseInt(option.order));
        })

        break;
      case "2":
        if (this.state.optionList.length <= 0) {
          this.props.setNotification({
            message: 'Please Fill Up',
            hasError: true,
            timestamp: new Date().getTime()
          });
          return;
        }
        let error = false;
        this.state.optionList.forEach((option) => {
          if (option.option.length <= 0 || option.order.length <= 0) {
            error = true;
          }
        });
        if (error) {
          this.props.setNotification({
            message: 'Please Fill Up',
            hasError: true,
            timestamp: new Date().getTime()
          });
          return;
        }
        formData.append('original_img', '');
        this.state.optionList.forEach((option) => {
          formData.append('puzzle_option[]', option.option);
          formData.append('puzzle_order[]', option.order);
        })

        break;
      case '4':
        if (this.state.pushGame.length <= 0) {
          this.props.setNotification({
            message: 'Please Fill Up',
            hasError: true,
            timestamp: new Date().getTime()
          });
          return;
        }
        let errors = false;
        this.state.pushGame.forEach((option) => {
          if (option.option.length <= 0 || option.order.length <= 0 || option.file == '') {
            errors = true;
          }
        });
        if (errors) {
          this.props.setNotification({
            message: 'Please Fill Up',
            hasError: true,
            timestamp: new Date().getTime()
          });
          return;
        }
        this.state.pushGame.forEach((option) => {
          formData.append('puzzle_img_path[]', option.file);
          formData.append('puzzle_order[]', parseInt(option.order));
          formData.append('puzzle_option[]', option.option);
        });
        break;
      default:
        break;
    }
    const url = this.props.url_game.SAVE_AND_UPDATE_GAMIFICATION;
    this.props.ajaxUtil.sendRequest(url, formData, response => {
      this.props.setNotification({
        message: response.message,
        hasError: !response.status,
        timestamp: new Date().getTime()
      });
      if (response.status) {
        this.props.closeModal();
      }
    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });
  }
  reset = () => {
    this.setState({
      optionList: [''],
      gameType: '',
      gameName: '',
      challenges: '',
      clue: '',
      minutes: '',
      originalFile: '',
      imageOptions: [{ file: '', order: '' }],
      pushGame: [{ file: '', order: '', option: '' }]
    })
  }
  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <div className="form-group">
              <label htmlFor="Game Type">Game Type</label>
              <select onChange={(e) => this.handleChange(e)} value={this.state.gameType} className="form-control required" name="gameType">
                <option value="">Select</option>
                {this.state.gameTypes.map((type) => (
                  <option value={type.id}>{type.game_type_name}</option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="Game Name">Game Name</label>
              <input
                onChange={(e) => this.handleChange(e)}
                className="form-control required"
                value={this.state.gameName}
                name="gameName" />
            </div>
            <RenderGameFields
              addPushGame={this.addPushGame}
              deletedPushGame={this.deletedPushGame}
              handlePushGameChange={this.handlePushGameChange}
              pushGame={this.state.pushGame}
              optionChange={this.optionChange}
              addOption={this.addOption}
              deletedOption={this.deletedOption}
              optionList={this.state.optionList}
              originalFile={this.state.originalFile}
              deletedImageOption={this.deletedImageOption}
              addImageOption={this.addImageOption}
              imageOptions={this.state.imageOptions}
              handleImagePartChange={this.handleImagePartChange}
              originalImageChange={this.originalImageChange}
              handleChange={this.handleChange}
              type={this.state.gameType} />
            <div className="form-group">
              <label htmlFor="Game Challenge">Add Challenges</label>
              <input
                onChange={(e) => this.handleChange(e)}
                className="form-control required"
                value={this.state.challenges}
                name="challenges" />
            </div>
            <div className="form-group">
              <label htmlFor="Game Clue">Game Clue</label>
              <input
                onChange={(e) => this.handleChange(e)}
                className="form-control required"
                value={this.state.clue}
                name="clue" />
            </div>
            <div className="form-group">
              <label htmlFor="Minutes">Minutes</label>
              <input
                onChange={(e) => this.handleChange(e)}
                className="form-control required"
                value={this.state.minutes}
                name="minutes" />
            </div>
          </div>
          <div className="col-md-12 text-right">
            <button type="button" className="btn btn-danger mr-1 ml-1 cancel hide" onClick={this.props.closeModal}>Cancel</button>
            <button type="button" className="btn btn-danger mr-1 ml-1 reset hide" onClick={this.reset} >Reset</button>
            <button type="button" className="btn btn-danger mr-1 ml-1 add hide" onClick={this.save}>Add</button>
          </div>
        </div>
      </div>
    )
  }
}

function RenderGameFields(props) {
  switch (props.type) {
    case "1":
    case "3":
      return (
        <Fragment>
          <div className="file__upload__wraper mb-3">
            <h6 className="label">Original Image</h6>
            <div className="file__upload__content">
              <div className="custom-file">
                <input type="file" onChange={e => props.originalImageChange(e)} className="custom-file-input" name="customFile" />
                <label className="custom-file-label" htmlFor="customFile">{props.originalFile.name ? props.originalFile.name : 'Choose file'}</label>
              </div>
            </div>
          </div>
          {
            props.imageOptions.map((imageOption, index) => (

              <div className="file__upload__wraper mb-3">
                <h6 className="label">File Upload & Order</h6>
                <div className="file__upload__content d-flex">
                  <div className="custom-file">

                    <input onChange={(event) => props.handleImagePartChange(event, index)} type="file" className="custom-file-input" name="file" />
                    <label className="custom-file-label" htmlFor="file"> {imageOption.file.name ? imageOption.file.name : 'Choose file'}</label>
                  </div>
                  <div className="order">
                    <input value={imageOption.order} name="order" onChange={(event) => props.handleImagePartChange(event, index)} type="text" placeholder="Order" className="form-control mx-2" />
                  </div>
                  <div className="d-flex">
                    <button className="action__btn btn btn_add">
                      <img onClick={props.addImageOption} src="/dms/images/plus.svg" alt="add" />
                    </button>
                    <button className="action__btn btn btn_remove">
                      <img onClick={() => props.deletedImageOption(index)} src="/dms/images/minus.svg" alt="remove" />
                    </button>
                  </div>
                </div>
              </div>
            ))
          }

        </Fragment>
      );
    case "2":
      return (
        <Fragment>
          {
            props.optionList.map((option, index) => (
              <div className="file__upload__wraper mb-3">
                <h6 className="label">Option</h6>
                <div className="file__upload__content d-flex">

                  <div className="order">
                    <input value={option.option} name="option" onChange={(event) => props.optionChange(event, index)} type="text" placeholder="Option" className="form-control mx-2" />
                  </div>
                  <div className="order mr-2 ml-2">
                    <input value={option.order} name="order" onChange={(event) => props.optionChange(event, index)} type="text" placeholder="Order" className="form-control mx-2" />
                  </div>
                  <div className="d-flex">
                    <button
                      onClick={props.addOption}
                      className="action__btn btn btn_add">
                      <img src="/dms/images/plus.svg" alt="add" />
                    </button>
                    <button
                      onClick={() => props.deletedOption(index)}
                      className="action__btn btn btn_remove">
                      <img src="/dms/images/minus.svg" alt="remove" />
                    </button>
                  </div>
                </div>
              </div>
            ))
          }
        </Fragment>
      );
    case "4":
      return (
        <Fragment>
          {
            props.pushGame.map((option, index) => (
              <div className="file__upload__wraper mb-3">
                <h6 className="label">Option</h6>
                <div className="file__upload__content d-flex">
                  <div className="custom-file">

                    <input onChange={(event) => props.handlePushGameChange(event, index)} type="file" className="custom-file-input" name="file" />
                    <label className="custom-file-label" htmlFor="file"> {option.file.name ? option.file.name : 'Choose file'}</label>
                  </div>
                  <div className="order">
                    <input value={option.option} name="option" onChange={(event) => props.handlePushGameChange(event, index)} type="text" placeholder="Option" className="form-control mx-2" />
                  </div>
                  <div className="order mr-2 ml-2">
                    <input value={option.order} name="order" onChange={(event) => props.handlePushGameChange(event, index)} type="number" min="0" placeholder="Answer" className="form-control mx-2" />
                  </div>
                  <div className="d-flex">
                    <button
                      onClick={props.addPushGame}
                      className="action__btn btn btn_add">
                      <img src="/dms/images/plus.svg" alt="add" />
                    </button>
                    <button
                      onClick={() => props.deletedPushGame(index)}
                      className="action__btn btn btn_remove">
                      <img src="/dms/images/minus.svg" alt="remove" />
                    </button>
                  </div>
                </div>
              </div>
            ))
          }
        </Fragment>
      )
    default:
      return (
        <div></div>
      )
      break;
  }
}
