import React, { Component, Fragment } from 'react';
import { Link, Switch, Route } from 'react-router-dom';
import ApprovedLocation from './Approved';
import { Popup, POPUP_ALIGN } from '@6d-ui/popup';
import AddLocation from './AddLocation';
export default class Location extends Component {

  constructor(props) {
    super(props);
    this.state = {
      approvedLocationList: [],
      approvedLocationPagination: {},
      addLocationModel: false,
      selectedItem: {},
      search: '',
    }
  }
  componentDidMount() {
    this.getApprovedLocationList();
    this.props.setActive('location');
  }

  getApprovedLocationList = () => {
    const url = this.props.url_training.APPROVED_LOCATION + `?page=1&size=1000&sort=id&order=desc&keyword=`;
    this.props.ajaxUtil.sendRequest(url, {}, approvedLocation => {
      this.setState({ approvedLocationPagination: approvedLocation })
      if (approvedLocation.hasOwnProperty('data')) {
        if (approvedLocation.data != null && approvedLocation.data.length > 0) {
          this.setState({ approvedLocationList: approvedLocation.data });
        }
      }
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }

  onEditItem = (item) => {
    this.setState({ selectedItem: item })
    this.setState({ addLocationModel: true })

  }

  onChangeStatus = (item) => {
    this.props.ajaxUtil.sendRequest(this.props.url_training.LOCATION_STATUS_CHANGE, { id: item.id }, status => {
      let error = true;
      if (status.statusCode == 200) {
        error = false;
      }
      this.props.setNotification({
        message: status.message,
        hasError: error,
        timestamp: new Date().getTime()
      });
      this.getApprovedLocationList();
    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });
  }

  openAddLocationModel = () => {
    this.setState({ addLocationModel: true })
    this.setState({ selectedItem: {} })

  }
  closeAddLocationModel = () => {
    this.setState({ addLocationModel: false })
  }
  onDeleteLocation = (location) => {
    if (window.confirm("Do you want to delete")) {
      this.props.ajaxUtil.sendRequest(this.props.url_training.DELETE_LOCATION, { id: location.id }, status => {
        let error = true;
        if (status.statusCode == 200) {
          error = false;
        }
        this.props.setNotification({
          message: status.message,
          hasError: error,
          timestamp: new Date().getTime()
        });
        this.getApprovedLocationList();
      }, this.props.loadingFunction,
        { method: 'POST', isShowSuccess: false });

    }
  }
  reset = () => {
    this.setState({ selectedItem: {} })
  }
  timeInterval = '';
  search = (event) => {
    this.setState({ search: event.currentTarget.value });
    clearInterval(this.timeInterval);
    this.timeInterval = setTimeout(() => {
      this.performSearch()
    }, 500)
  }
  performSearch = () => {
    if (this.state.search.length > 0) {
      const url = this.props.url_training.APPROVED_LOCATION + `?page=1&size=1000&sort=id&order=desc&keyword=${this.state.search}`;
      this.props.ajaxUtil.sendRequest(url, {}, approvedLocation => {
        this.setState({ approvedLocationPagination: approvedLocation })
        if (approvedLocation.hasOwnProperty('data')) {
          if (approvedLocation.data != null && approvedLocation.data.length > 0) {
            this.setState({ approvedLocationList: approvedLocation.data });
          }
          if(approvedLocation.data == null) {
            this.setState({ approvedLocationList: [] });
          }
        }
      }, this.props.loadingFunction,
        { method: 'GET', isShowSuccess: false });
    } else {
      this.getApprovedLocationList();
    }
  }
  render() {
    const { path, url, params } = this.props.match;
    const { approvedLocationList, addLocationModel, selectedItem } = this.state;
    const { search, reset, onEditItem, openAddLocationModel, closeAddLocationModel, onChangeStatus, getApprovedLocationList, onDeleteLocation } = this;
    const { url_training, ajaxUtil, loadingFunction, setNotification } = this.props;

    return (
      <Fragment>
        <ApprovedLocation
          openAddLocationModel={openAddLocationModel}
          approvedLocationList={approvedLocationList}
          onChangeStatus={onChangeStatus}
          onEditItem={onEditItem}
          onDeleteLocation={onDeleteLocation}
          search={search}
        />
        <Popup
          type={POPUP_ALIGN.RIGHT}
          title={selectedItem.hasOwnProperty("id") ? "Update Location" : "Add Location"}
          isOpen={addLocationModel}
          close={closeAddLocationModel}
          minWidth="90%"
          component={
            <AddLocation
              closeAddLocationModel={closeAddLocationModel}
              url_training={url_training}
              ajaxUtil={ajaxUtil}
              loadingFunction={loadingFunction}
              setNotification={setNotification}
              getApprovedLocationList={getApprovedLocationList}
              selectedItem={selectedItem}
              onDeleteLocation={onDeleteLocation}
              reset={reset}
            />
          }
        />
      </Fragment>

    )
  }
}
