import React, { Component } from 'react';
import moment from 'moment';
function RenderOptions(props) {
  const { data, keyValue, value } = props;
  if (data.length > 0) {
    let options = data.map((option, index) => (
      <option key={option[value] + index} value={option[keyValue]}> {option[value]} </option>
    ))
    return [<option key="-10" defaultValue value="" disabled> Please Select</option>, ...options];
  }
  return (
    <option value=''>Please Select</option>
  )
}
export default class ScheduleTraining extends Component {

  constructor(props) {
    super(props);
    this.state = {
      isEdit: false,
      trainingTypeList: [],
      approvedLocationList: [],
      trainersList: [],
      trainingType: {
        trainingType: '',
        trainingName: '',
        trainingLocation: '',
        trainingDate: '',
        maxParticipants: '',
        trainingTime: '',
        mode: '1',
        endDate: '',
        endTime: '',
        trainer: '',
        traininginterval: 1,
        passingCriteria: ''
      },
      infraRequirements: ['', '']
    }
  }
  componentDidMount() {
    this.props.setHeader("Training Management");
    this.getTrainingTypes();
    this.getLocations();
    this.getTrainers();
    document.getElementById("endData").setAttribute("min", moment().format("YYYY-MM-DD"));
    document.getElementById("trainingDate").setAttribute("min", moment().format("YYYY-MM-DD"));
    this.checkIsEdit();

  }
  checkIsEdit = () => {
    if (this.props.match.params.hasOwnProperty("edit") && this.props.match.params.edit !== undefined) {

      this.setState({ isEdit: true })
      const url = this.props.url_training.GET_SELECTED_TRAINING_DATA;
      this.props.ajaxUtil.sendRequest(url, {
        id: this.props.match.params.edit
      }, schedule => {
        const response = schedule.data[0];
        const data = {
          trainingType: response.training_type_id,
          trainingName: response.trainingname,
          trainingLocation: response.location_master_id,
          trainingDate: response.from_date,
          maxParticipants: response.participantcnt,
          trainingTime: response.from_time,
          mode: '1',
          endDate: response.to_date,
          endTime: response.to_time,
          trainer: response.trainer,
          passingCriteria: response.passingcriteria,
          traininginterval: response.traininginterval
        }

        this.setState({ trainingType: data })
      }, this.props.loadingFunction,
        { method: 'POST', isShowSuccess: false });

      // get requirements
      const reqUrl = this.props.url_training.GET_SELECTED_SCHEDULE_REQUIREMENTS;

      this.props.ajaxUtil.sendRequest(reqUrl, {
        id: this.props.match.params.edit
      }, requirements => {
        if (requirements.data) {
          const infraRequirements = requirements.data.map((req) => req.requirement);
          this.setState({ infraRequirements })
        }

      }, this.props.loadingFunction,
        { method: 'POST', isShowSuccess: false });
    } else {
      this.setState({ isEdit: false })
    }
  }
  getTrainers = () => {
    const url = this.props.url_training.GET_TRAINER;
    this.props.ajaxUtil.sendRequest(url, {}, trainersList => {
      if (trainersList.hasOwnProperty('data')) {
        if (trainersList.data != null && trainersList.data.length > 0) {
          this.setState({ trainersList: trainersList.data });
        }
      }
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }
  getTrainingTypes = () => {
    const url = this.props.url_training.LIST_TRAINING + `?page=1&size=1500&sort=id&status=1&order=desc&keyword=`;
    this.props.ajaxUtil.sendRequest(url, {}, trainingList => {
      this.setState({ trainingListPagination: trainingList })
      if (trainingList.hasOwnProperty('data')) {
        if (trainingList.data != null && trainingList.data.length > 0) {
          this.setState({ trainingTypeList: trainingList.data });
        }
      }
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }
  getLocations = () => {
    const url = this.props.url_training.APPROVED_LOCATION + `?page=1&size=1000&sort=id&status=1&order=desc&keyword=`;
    this.props.ajaxUtil.sendRequest(url, {}, approvedLocation => {
      this.setState({ approvedLocationPagination: approvedLocation })
      if (approvedLocation.hasOwnProperty('data')) {
        if (approvedLocation.data != null && approvedLocation.data.length > 0) {
          this.setState({ approvedLocationList: approvedLocation.data });
        }
      }
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }
  handleChange = (event) => {
    let data = { ...this.state.trainingType };
    data[event.currentTarget.name] = event.currentTarget.value;
    this.setState({ trainingType: data });
  }
  onRequirementChange = (event, index) => {
    let requirements = [...this.state.infraRequirements];
    requirements[index] = event.currentTarget.value;
    this.setState({ infraRequirements: requirements });
  }
  addRequirement = () => {
    let requirements = [...this.state.infraRequirements];
    requirements.push('');
    this.setState({ infraRequirements: requirements });
  }
  removeRequirement = (index) => {
    let requirements = [...this.state.infraRequirements];
    requirements.splice(index, 1);
    this.setState({ infraRequirements: requirements });
  }
  resetForm = () => {
    this.setState({
      trainingType: {
        trainingType: '',
        trainingName: '',
        trainingLocation: '',
        trainingDate: '',
        maxParticipants: '',
        trainingTime: '',
      }
    })
    this.setState({ infraRequirements: ['', ''] })
  }
  submitForm = () => {
    let keys = Object.keys(this.state.trainingType);
    let error = false;

    keys.forEach((key) => {
      if (this.state.trainingType[key] == null || this.state.trainingType[key].toString().trim().length <= 0) {
        error = true;
      }
    });
    if (error) {
      this.props.setNotification({
        message: 'Please Fill Up',
        hasError: true,
        timestamp: new Date().getTime()
      });
      return;
    }

    //save
    let data = {
      trainingname: this.state.trainingType.trainingName,
      location: this.state.trainingType.trainingLocation,
      startdate: moment(this.state.trainingType.trainingDate).format('YYYY-MM-DD'),
      enddate: moment(this.state.trainingType.endDate).format('YYYY-MM-DD'),
      traininginterval: this.state.trainingType.traininginterval,
      mode: this.state.trainingType.mode,
      trainingtype: this.state.trainingType.trainingType,
      participantcnt: this.state.trainingType.maxParticipants,
      starttime: this.state.trainingType.trainingTime,
      endtime: this.state.trainingType.endTime,
      passingcriteria: this.state.trainingType.passingCriteria,
      trainer: this.state.trainingType.trainer,
      requirment: this.state.infraRequirements.filter((req) => req.length > 0)
    }
    let url = this.props.url_training.SAVE_TRAINING_TYPE;
    if (this.state.isEdit) {
      data['id'] = this.props.match.params.edit;
      data['mode'] = 1;
      url = this.props.url_training.UPDATE_SELECTED_SCHEDULE
    }
    this.props.ajaxUtil.sendRequest(url, data, response => {
      this.props.setNotification({
        message: response.message,
        hasError: !response.status,
        timestamp: new Date().getTime()
      });
      if (response.status) {
        if (this.state.isEdit) {
          this.props.history.push(`/Dashboard/Schedule/Training/Quiz/${this.props.match.params.edit}/edit`)
        } else {
          this.props.history.push('/Dashboard/Schedule/Training/Quiz/' + response.data)
        }
      }
    }, this.props.loadingFunction,
      { method: 'POST', isShowSuccess: false });
  }
  render() {
    const { infraRequirements, trainingTypeList, approvedLocationList, trainingType } = this.state;
    const { submitForm, resetForm, handleChange, onRequirementChange, addRequirement, removeRequirement } = this;
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <div className="progress__bar mt-4">
              <div className="steps Bullet">
                <div className="bullet">

                  <span></span>

                </div>
                <div className="check fa fa-check"></div>

              </div>
              <div className="steps ">
                <div className="bullet"><span></span></div>
                <div className="check fa fa-check"></div>
              </div>

              <div className="steps">
                <div className="bullet"><span></span></div>
                <div className="check fa fa-check">

                </div>
              </div>
              <div className="steps">
                <div className="bullet"><span></span></div>
                <div className="check fa fa-check">

                </div>
              </div>
            </div>
          </div>
          <div className="col-md-12">

            <h4 className="sub__heading">Schedule Training</h4>
            <div className="row m-0">
              <div className="col-lg-6">
                <div className="form-group mb-30">
                  <label htmlFor="Training">Name</label>
                  <input
                    onChange={(event) => handleChange(event)}
                    value={trainingType.trainingName}
                    type="text"
                    className="form-control required"
                    name="trainingName"
                    placeholder="Training Type Name" />
                </div>
                <div className=" form-group mb-30">
                  <label>Training Location</label>
                  <select
                    onChange={(event) => handleChange(event)}
                    value={trainingType.trainingLocation}
                    className="form-control required Select select-paceholder"
                    name="trainingLocation">
                    <RenderOptions
                      data={approvedLocationList}
                      keyValue="id"
                      value="location"
                    />
                  </select>
                </div>
                <div className="form-group mb-30">
                  <label htmlFor="Training">Training Date</label>
                  <input
                    onChange={(event) => handleChange(event)}
                    value={trainingType.trainingDate}
                    type="date"
                    name="trainingDate"
                    id="trainingDate"
                    className="form-control required date"
                    placeholder="Select Date" />
                </div>
                <div className="form-group mb-30">
                  <label htmlFor="Training">Training End Date</label>
                  <input
                    id="endData"
                    onChange={(event) => handleChange(event)}
                    value={trainingType.endDate}
                    type="date"
                    name="endDate"
                    className="form-control required date"
                    placeholder="Select Date" />
                </div>
                <div className=" form-group mb-30">
                  <label>Trainer</label>
                  <select
                    onChange={(event) => handleChange(event)}
                    value={trainingType.trainer}
                    className="form-control required Select select-paceholder"
                    name="trainer">
                    <RenderOptions
                      data={this.state.trainersList}
                      keyValue="id"
                      value="name"
                    />
                  </select>
                </div>
                <div className=" form-group mb-30">
                  <label>Mode</label>
                  <select
                    onChange={(event) => handleChange(event)}
                    value={trainingType.mode}
                    className="form-control required Select select-paceholder"
                    name="mode">
                    <option value="">Please Select</option>
                    <option value="1">Offline</option>
                    <option value="2">Online</option>

                  </select>
                </div>
              </div>
              <div className="col-lg-6">
                <div className=" form-group mb-30">
                  <label>Training Type</label>
                  <select
                    onChange={(event) => handleChange(event)}
                    value={trainingType.trainingType}
                    className="form-control required Select select-paceholder"
                    name="trainingType">
                    <RenderOptions
                      data={trainingTypeList}
                      keyValue="id"
                      value="training_type"
                    />
                  </select>
                </div>
                <div className=" form-group mb-30">
                  <label>Max. Participant</label>
                  <input
                    onChange={(event) => handleChange(event)}
                    value={trainingType.maxParticipants}
                    type="number"
                    min="0"
                    className="form-control required Select select-paceholder"
                    name="maxParticipants" />
                </div>

                <div className="form-group mb-30">
                  <label htmlFor="Training">Training Time</label>
                  <input
                    onChange={(event) => handleChange(event)}
                    value={trainingType.trainingTime}
                    type="time"
                    name="trainingTime"
                    className="form-control required"
                    placeholder="Training Type Name" />
                </div>
                <div className="form-group mb-30">
                  <label htmlFor="Training">Training End Time</label>
                  <input

                    onChange={(event) => handleChange(event)}
                    value={trainingType.endTime}
                    type="time"
                    name="endTime"
                    className="form-control required"
                    placeholder="Training Type Name" />
                </div>
                <div className="form-group mb-30">
                  <label htmlFor="Training">Passing Criteria (Percentage)</label>
                  <input
                    onChange={(event) => handleChange(event)}
                    value={trainingType.passingCriteria}
                    type="text"
                    name="passingCriteria"
                    className="form-control required"
                    placeholder="Passing Criteria" />
                </div>
                <div className="form-group mb-30">
                  <label>Training interval </label>
                  <select
                    onChange={(event) => handleChange(event)}
                    value={trainingType.traininginterval}
                    className="form-control required Select select-paceholder"
                    name="traininginterval">
                    <option value="0">Select</option>
                    <option value="1">1 Month</option>
                    <option value="3">3 Month</option>
                    <option value="6">6 Month</option>
                    <option value="9">9 Month</option>
                    <option value="12">1 Year</option>
                  </select>
                </div>
              </div>
            </div>
            <div className="col-md-12">
              <div className="row">
                <div className="col-md-12">
                  <h4 className="sub__heading">
                    Infrastructure Requirement <span
                      className="text-muted">(Optional)</span>
                  </h4>
                </div>
                {
                  infraRequirements.map((requirement, index) => (
                    <div className="col-md-6" key={index}>
                      <div className="_infra">

                        <div className="form-group mb-30">
                          <label htmlFor="Training">Requirement {index + 1}</label>
                          <div className="d-flex">
                            <input
                              onChange={(event) => onRequirementChange(event, index)}
                              type="text"
                              value={requirement}
                              className="form-control"
                              placeholder="Requirement" />
                            <div className="d-flex">
                              <button
                                onClick={addRequirement}
                                className="btn">
                                <img src="/dms/images/plus.svg" alt="add" />
                              </button>
                              <button
                                onClick={() => removeRequirement(index)}
                                className="btn">
                                <img src="/dms/images/minus.svg" alt="remove" />
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))
                }

              </div>
            </div>
            <div className="footer__button__fixed text-right" >
              <div className="button__work">
                <button type="button" className="btn btn-danger btn__fade cancel">Cancel</button>
                <button type="button"
                  onClick={resetForm}
                  className="btn btn-danger btn__fade reset">Reset</button>
                <button type="button"
                  onClick={submitForm}
                  className="btn btn-danger btn__fade add next_btn">{(this.state.isEdit) ? 'Update' : 'Next'}</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
