import React, { Component, useState, useEffect } from 'react';
import { get, post } from 'axios'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { confirmAlert } from 'react-confirm-alert';
import { connect } from "react-redux";
import { ToastContainer, toast } from 'react-toastify';
import DataGrid from '../../../components/data-grid';
import { storeMaterials } from '../../../actions/Modules'
// import DataGrid from '../DataGrid';
import { CONSTANTS } from '../../../util/Constants'
import Question from './Question/index';
import { LABEL_LIST as DataTableHeader } from '../util/DataTableHeader';
import { trackPromise, usePromiseTracker } from 'react-promise-tracker';


const url_materials = CONSTANTS.MATERIALS;


class MaterialList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            module_id: this.props.module_id,
            parent_subject: this.props.parent_subject,
            materials: [],
            showQuestionModal: false,
            questionAction: 'CREATE',
            material_id: '',
            moduleName: '',
            filterdMaterial:[],
            totalMaterial:0,
            approvalStatus:1,
            rejection:{
                id:'',
                reason:'',
                Modal:false
            },
            reason_require:false
        }
    }
    componentDidMount() {
        const moduleList = this.props.state;
        const { module_id } = this.state;
        const moduleData = moduleList.filter((mod) => mod.subject_id == module_id);
        if (moduleData.length > 0) {
            this.setState({ moduleName: moduleData[0].subject_name });
        }

        this.getMaterialList(this.state.approvalStatus);
    }
    componentDidUpdate(prevProps) {
        const { isModuleListUpdated } = this.props;
        console.log('this.props ',this.props);
        if (prevProps.isModuleListUpdated !== isModuleListUpdated && isModuleListUpdated) {
            this.getMaterialList();
        }
    }

    reasonChangeHandler (e) {
        if(e.target.checkValidity()){
            this.setState({[e.target.name]:true})
            this.setState({rejection:{...this.state.rejection,reason:e.target.value}});
        }   
    }

    setRejectionId(id) {
        this.setState({rejection:{...this.state.rejection,id,modal:"show"}});
    }

    cancelModal(){
        this.setState({rejection:{...this.state.rejection, reason:'', modal:''}}, ()=>{})
    }

    async getMaterialList (approveStatus) {
        this.setState({approvalStatus:approveStatus})
        try {
            const materialListUrl = url_materials.LIST_URL + `?page=1&size=15&sort=material_id&status=&order=desc&keyword&module_id=${this.state.module_id}&approval_status=${approveStatus ? approveStatus : 1}`;
            const materialData = (await (trackPromise(get(materialListUrl)))).data;
            const materialTotal = materialData.totalElements
            const materialList = materialData.data
            this.setState({totalMaterial:materialTotal })
            if(materialData.statusCode == 200){
                const materials = [];
                if(materialList && materialList.length > 0){
                materialList.forEach(element => {
                    let dd = {};
                    dd = element;
                    dd.id = element.material_id;
                    dd.dataStatus = element.material_status;
                    dd.material_type = element.material_type == 'Promotion' ? 'Quiz' : element.material_type;
                    dd.subRowsCount = element.question_count ? element.question_count : 0;
                    materials.push(element);
                });
              }
                this.props.storeMaterials(materials);
                this.setState({ materials,filterdMaterial:materials });
            }else{
                toast.error(materialData.message);
            }
          } catch (e) {
            toast.error(e.message);
          }
        }   

    async onChangeSelect (id, materialStatus) {
        if(materialStatus == 2){
            if (!this.state.reason_require) {
                toast.error('Please enter reason');
                return;
              }
            try {
                const rejectionMessageUrl = `${url_materials.REJECTION_MESSAGE}`;
                const data = {
                    id:this.state.rejection.id,
                    message:this.state.rejection.reason,
                    type:"material"
                }
                const rejectionSuccess = (await (trackPromise(post(rejectionMessageUrl, data)))).data;
                this.getMaterialList("0");
                rejectionSuccess.statusCode == 200 ? toast.success(rejectionSuccess.message) : toast.error(rejectionSuccess.message)
                this.cancelModal()
                } catch (e) {
                toast.error('Network Error');
            }
        }
        try {
            const material_approve_url = `${url_materials.MATERIAL_APPROVE_STATUS}`;
            const data = {
                id:id,
                status:materialStatus
            }
            const matsuccess = (await (trackPromise(post(material_approve_url, data)))).data;
            this.getMaterialList("0");
            materialStatus == 1 ? toast.success('Material accepted successfully') : toast.error('Material rejected successfully')
            } catch (e) {
            toast.error(e.message);
        }
    }

    refreshList = () => {
        this.getMaterialList();
        this.setState({ showQuestionModal: false });
    }

    hideQuestionModal = () => {
        this.setState({ showQuestionModal: false });
    }
    getMaterialId = (material_id) => {
        this.setState({ material_id, showQuestionModal: true });
    }

    onChangeStatus = (id, materialStatus) => {
        const request = { material_id: id, status: materialStatus ? 1 : 0 };
        post(url_materials.UPDATE_STATUS_MATERIAL, request).then(response => {
            const res = response.data;
            if (res.status) {
                toast.success('Success: ' + res.message);
                this.getMaterialList();
            } else {
                toast.error('Error: ' + res.message);
            }
        }, (error) => {
            toast.error('Error: Bad request.');
        })
    }
    onOpenModal = (id) => {
        this.setState({ showQuestionModal: true, questionAction: 'CREATE', material_id: id });
    }

    onEdit = (id) => {
        this.props.onEditMaterial(id, this.state.approvalStatus)
    }

    deleteModuleConfirm(id) {
        this.props.setModalPopup({
            'rowId': id,
            'isOpen': true,
            'onConfirmCallBack': () => this.deleteMaterial(id),
            'title': "Confirm Delete",
            'content': "Are you sure to Delete ?",
            'CancelBtnLabel': "Cancel",
            'confirmBtnLabel': "Delete"
          });
    }

    deleteMaterial = (id) => {
        post(url_materials.DELETE_URL,
            { id }).then(response => {
                const res = response.data;
                if (res.statusCode == 200) {
                    toast.success('Success: ' + res.message);
                    this.getMaterialList(this.state.approvalStatus);
                } else {
                    toast.error('Error: ' + res.message);
                }
            }, (error) => {
                toast.error('Error: Bad request.');
            })
    }
    onGetList = (id) => {
        this.setState({ showQuestionModal: true, questionAction: 'LIST', material_id: id });
    }

    filterMaterials = (filterQuery) => {
        let { materials } = this.state;
        let filterdMaterial = materials;
        if (filterQuery.length > 0) {
            filterdMaterial = materials.filter((material) => material.material_name.toLowerCase().indexOf(filterQuery.toLowerCase()) !== -1);
        }
        this.setState({
            filterdMaterial
        })
    }


    render() {
        const { materials, rejection, approvalStatus, filterdMaterial, totalMaterial, showQuestionModal, material_id, questionAction, moduleName } = this.state;
        return (
            <div className="h-100 mainbg material-tab-wapper col-md-50">
                <div className="material-header d-flex justify-content-between ">
                    <h4>Material</h4>
                    <img src={process.env.PUBLIC_URL + "/images/close.svg"} alt="" onClick={() => this.props.closeMaterialList()} />
                </div>
                <div className="mainbg  cus-pills">
                    <ul className="nav nav-pills">
                        <li className="nav-item" onClick={() => this.getMaterialList(1)}>
                            <a className="nav-link approved-link active " data-toggle="pill"
                                href="#approved">Approved</a>
                        </li>
                        <li className="nav-item" >
                            <a className="nav-link" data-toggle="pill" href="#pending" onClick={() => this.getMaterialList("0")}>Pending</a>
                        </li>
                        <li className="nav-item" >
                            <a className="nav-link" data-toggle="pill" href="#rejected" onClick={() => this.getMaterialList(2)}>Rejected</a>
                        </li>
                    </ul>
                    <div className="tab-content pzero">
                        <div className="tab-pane active approved" id="approved">

                            <div className="Modules-txt h-100 ">
                                <h4 className="title">Module: <b>{moduleName}</b> <span>(Active)</span></h4>
                                <div className="d-flex mb-10 modulesheader mt-30">
                                 <div className="col-md-6">
                                        <div className="d-flex entities-box align-items-center">
                                            <h4>All Entities | {totalMaterial > 0 ? (totalMaterial < 10 ? `0${totalMaterial}` : totalMaterial) : 0} </h4>
                                        {approvalStatus != "0" &&  <button className="btn add" onClick={() => this.props.onOpenMaterialModal(this.props.module_id)}>Add Material</button> }
                                    </div>
                                    </div>

                                    <div className="col-md-6 d-flex justify-content-end">
                                        <div className="searchbox ">
                                            <input type="search"
                                                placeholder="What are you looking for ?"  onChange={(e)=>this.filterMaterials(e.target.value )}/>
                                            <button className="btn"><i className="fa fa-search"
                                                aria-hidden="true"></i></button>
                                        </div>
                                        <div className="filter">
                                            <img src={process.env.PUBLIC_URL+"/images/filter.svg"} alt="filter" />
                                        </div>
                                    </div>
                                </div>
                                <div className="table-responsive">
                                    <DataGrid
                                        columns={approvalStatus == "0" ? DataTableHeader.MATERIAL_PENDING : DataTableHeader.MATERIAL}
                                        data={filterdMaterial}
                                        onChangeStatus={(id, status) => this.onChangeStatus(id, status)}
                                        onOpenModal={(id) => this.onOpenModal(id)}
                                        onEdit={(id) => this.onEdit(id)}
                                        onDelete={(id) => this.deleteModuleConfirm(id)}
                                        onGetList={(id) => this.onGetList(id)}
                                        onChangeSelect={(id, status) => this.onChangeSelect(id,status)}
                                        setRejectionId={(id) => this.setRejectionId(id)}
                                        onOpenMaterialViewModal = {(id) => this.props.onOpenMaterialViewModal(id)}
                                    />
                                </div>
                                {showQuestionModal &&
                                <Question
                                    {...this.props}
                                    showQuestionModal={showQuestionModal}
                                    material_id={material_id}
                                    refreshList={this.refreshList}
                                    questionAction={questionAction}
                                    hideQuestionModal={this.hideQuestionModal}
                                />
                                }   
                            </div>
                        </div>
                    </div>
                </div>
              <div>

  <div className={`modal fade ${rejection.modal}`} id="rejectModal" style={{ display: `${rejection.modal ? 'block' : 'none'}` }} tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div className="modal-dialog" role="document">
      <div className="modal-content">
        <div className="modal-header">
          <h5 className="modal-title" id="exampleModalLabel">Reason</h5>
          <button type="button" className="close" onClick={() => this.cancelModal()} aria-label="Close">
            <span aria-hidden="true" >×</span>
          </button>
        </div>
        <div className="modal-body">
        <form noValidate>
              <div className="form-group form_control_space margin_bottom_1rem">
                <label htmlFor="formGroupExampleInput"></label>
                <input
                  type="text"
                  className="form-control required custom_textarea"
                  id="formGroupExampleInput"
                  placeholder="Enter Reason" name="reason_require"
                  onChange={(e) => this.reasonChangeHandler(e)}
                  value={this.state.rejection.reason}
                  required
                />
              </div>
            </form>
        </div>
        <div className="modal-footer">
          <button type="button" className="btn btn-secondary" onClick={() => this.cancelModal()} data-dismiss="modal">Cancel</button>
          <button type="button" className="btn btn-primary" onClick={() => this.onChangeSelect(rejection.id, 2)}>Submit</button>
        </div>
      </div>
    </div>
  </div>
</div>

            </div>
        )
    }
}
export default connect((state) => ({ state: state.moduleList }), { storeMaterials })(MaterialList);
