import React, { Component } from 'react'
import { Container, Row, Col, Nav, NavItem, NavLink, TabContent, TabPane } from 'reactstrap';
import ListSyllabus from './ListSyllabus'
import ListModule from './ListModule';
import DashboardForm from './DashboardForm';
import { CONSTANTS } from '../../util/Constants'
import MaterialList from './Modal/MaterialList'
import MaterialAdd from "./Modal/MaterialAdd";
import Loader from 'react-promise-loader';
import { trackPromise, usePromiseTracker } from 'react-promise-tracker';
import { ToastContainer, toast } from 'react-toastify';
import MaterialView from './Modal/MaterialView';
import { Link, Switch, Route } from 'react-router-dom';

const url_syllabus = CONSTANTS.SYLLABUS;

export default class index extends Component {
    constructor(props) {
        super(props)
        this.state = {

        }
    }
    componentDidMount() {
        // this.fetchSyllabus()

    }

    render() {
        const { showAddSyllabusModal, syllabusDetail, toalPost,
            showAddMaterialModal,
            posts,
            showMaterialListModal,
            isModuleListUpdated,
            material_id, approval_status, showMaterialViewModal,
            module_id } = this.state;
        return (
            <div className="custom-container dashborad">
                  <Loader promiseTracker={usePromiseTracker} />
                <Container fluid>
                    <Row>
                        <Col>
                            <div>
                                <div className="container-fluid">
                                    <div className="row syllabus-details">
                                        <DashboardForm 
                                            {...this.props}
                                            posts={posts}
                                            toalPost={toalPost}
                                            loadDetail={(subject_id) => this.loadDetail(subject_id)}
                                            syllabusDetail={syllabusDetail}
                                            updatePosts={(data) => this.updatePosts(data)}
                                            showAddSyllabusModal={this.showAddSyllabusModal}
                                        />
                                        <div className="col-md-8">
                                            {/* {!showMaterialListModal && !showMaterialViewModal && */}
                                            <div className="mainbg cus-pills">

                                                <ul className="nav nav-pills">
                                                    <li className="nav-item">
                                                        <a className="nav-link active" data-toggle="pill" href="#Details">
                                                            Details</a>
                                                    </li>
                                                    <li className="nav-item">
                                                        <a className="nav-link" data-toggle="pill" href="#Modules">
                                                            Modules</a>
                                                    </li>
                                                </ul>
                                                <div className="tab-content">
                                                    <ListSyllabus 
                                                     {...this.props}
                                                    syllabusDetail={syllabusDetail}
                                                    updatePosts={() => this.updatePosts()}
                                                    showAddSyllabusModal={showAddSyllabusModal} />
                                                    {syllabusDetail ? 
                                                    <ListModule 
                                                        {...this.props}
                                                        parentSubject={syllabusDetail}
                                                        isModuleListUpdated={isModuleListUpdated}
                                                        showMaterialList={(module_id) => this.showMaterialList(module_id)}
                                                        onOpenMaterialModal={(module_id) => { this.onOpenMaterialModal(module_id) }}
                                                    />
                                                    : ''}
                                                </div>
                                            </div>
                                            {showMaterialListModal && !showMaterialViewModal && 
                                                <div className="material-list-popup fullWidthPopup">
                                                    <MaterialList
                                                        {...this.props}
                                                        module_id={module_id} 
                                                        closeMaterialList={this.closeMaterialList}
                                                        isModuleListUpdated={isModuleListUpdated}
                                                        onOpenMaterialModal={(module_id) => this.onOpenMaterialModal(module_id)}
                                                        onOpenMaterialViewModal={(material_id) =>this.onOpenMaterialViewModal(material_id)}
                                                        onEditMaterial={(material_id,approval_status) => this.onEditMaterial(material_id,approval_status)} 
                                                        />
                                                </div>
                                            }
                                            { showMaterialViewModal && !showMaterialListModal &&
                                                <div className="material-list-popup fullWidthPopup fullheightPopup">
                                                        <MaterialView
                                                            {...this.props}
                                                            closeMaterialShow={this.closeMaterialShow}
                                                            material_id={material_id}

                                                        />
                                                </div>
                                            }

                                            <MaterialAdd
                                                {...this.props}
                                                showAddMaterialModal={showAddMaterialModal}
                                                module_id={module_id}
                                                material_id={material_id}
                                                approval_status={approval_status}
                                                materialId={material_id}
                                                cancelMaterialModal={(status) => this.cancelMaterialModal(status)}
                                            />

                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}
