import React, { useState, useEffect } from 'react';
import AddQuestion from './AddQuestion';
import ListQuestion from './ListQuestion';

const Question = (props) => {
    const {
        showQuestionModal,
        material_id,
        questionAction
    } = props;
    const [questionId, setQuestionId] = useState('');
    const [editAction, setEditAction] = useState(false);

    const editQuestion = (questionId) => {
        setEditAction(true);
        setQuestionId(questionId);
    }
    const resetAction = () => {
        setEditAction(false)
        setQuestionId('');
    }
    return (
        <div className={`modal cus-Syllabus-modal cus-material-modal fade ${showQuestionModal ? 'show custom-modal-open' : ''}`} style={{ display: `${showQuestionModal ? 'block' : 'none'}` }} id="addQuestion">
            <div className="modal-dialog row">
                <div className="col-md-2 cusoverlay"></div>
                <div className="modal-content col-md-10 pzero">
                    {(questionAction === 'CREATE' || editAction) &&
                        <AddQuestion
                            {...props}
                            material_id={material_id}
                            questionId={questionId}
                            hideQuestionModal={() => { resetAction(); props.hideQuestionModal() }}
                            refreshList={() => { resetAction(); props.refreshList() }}
                        />
                    }
                    {(questionAction === 'LIST' && !editAction) &&
                        <ListQuestion
                            {...props}
                            material_id={material_id}
                            hideQuestionModal={() => { resetAction(); props.hideQuestionModal() }}
                            refreshList={() => { resetAction(); props.refreshList() }}
                            editQuestion={(questionId) => editQuestion(questionId)}
                        />
                    }
                </div>
            </div>
        </div>
    );
}

export default Question;
