import React, { Component } from 'react';
import { Popup, POPUP_ALIGN } from '@6d-ui/popup';

export default class QuestionSet extends Component {
  constructor(props) {
    super(props);
    this.state = {
      addModel: false,
      isEdit: false,
      numberOfQuestion: 0,
      numberOfQuestionSet: '',
      numberOfEasyQuestion: '',
      numberOfMediumQuestion: '',
      numberOfHardQuestion: '',
      assessment_id: '',
    }
  }
  componentDidMount() {
    this.isEdit();
  }
  isEdit = () => {

    if (this.props.match.params.hasOwnProperty("edit") && this.props.match.params.edit !== undefined ) {
      this.setState({ isEdit: true });
      const url = this.props.url_training.GET_QUESTION_SET_DATA;
      this.props.ajaxUtil.sendRequest(url, {
        assessment_id: this.props.match.params.schedule_id
      }, response => {
        this.setState({
          assessment_id: response.data.assessment_id,
          numberOfQuestion: response.data.question_count,
          numberOfQuestionSet: response.data.question_set_count,
          numberOfEasyQuestion: response.data.easy,
          numberOfMediumQuestion: response.data.medium,
          numberOfHardQuestion: response.data.hard,
        })
      }, this.props.loadingFunction,
        { method: 'POST', isShowSuccess: false });
    }
  }
  closeModel = () => {
    this.setState({ addModel: false })
  }
  openModel = () => {
    this.setState({ addModel: true })
  }
  handleChange = (event) => {
    this.setState({ [event.currentTarget.name]: event.currentTarget.value });
  }
  reset = () => {
    this.setState(prevState => ({
      numberOfQuestion: '',
      numberOfQuestionSet: '',
      numberOfEasyQuestion: '',
      numberOfMediumQuestion: '',
      numberOfHardQuestion: '',
    }))
  }
  save = () => {
    let error = false;
    if (this.state.numberOfQuestion.trim().length <= 0 ||
      this.state.numberOfQuestionSet.trim().length <= 0 ||
      this.state.numberOfEasyQuestion.trim().length <= 0 ||
      this.state.numberOfMediumQuestion.trim().length <= 0 ||
      this.state.numberOfHardQuestion.trim().length <= 0
    ) {
      error = true;
    }
    if (error) {
      this.props.setNotification({
        message: 'Please Fill Up',
        hasError: true,
        timestamp: new Date().getTime()
      });
      return;
    }
    let data = {
      assessment_id: this.state.assessment_id,
      material_id: this.props.match.params.material_id.split("-"),
      training_type_id: 1,
      training_id: this.props.match.params.schedule_id,
      type: 'training',
      no_of_question: this.state.numberOfQuestion,
      no_of_question_set: this.state.numberOfQuestionSet,
      test_type: 'Post',
      easy: this.state.numberOfEasyQuestion,
      hard: this.state.numberOfHardQuestion,
      medium: this.state.numberOfMediumQuestion,
    }
    let url = this.props.url_training.CREATE_QUESTION_SET;
    if (this.state.isEdit) {
      url = this.props.url_training.UPDATE_QUESTION_SET_DATA;
    }
