import React, { Component } from 'react';
import { Popup, POPUP_ALIGN } from '@6d-ui/popup';
import AddOrEditGame from './AddOrEditGame';

export default class ScheduleList extends Component {

  constructor(props) {
    super(props);
    this.state = {
      gameList: [],
      showAddModel: false,
      isEdit: false,
    }
  }
  componentDidMount() {
    this.getGameScheduleList();
    this.props.setHeader("Gamification");

  }

  openModal = () => {
    this.setState({ showAddModel: true })
  }
  closeModal = () => {
    this.setState({ showAddModel: false })
    this.getGameScheduleList();
  }

  getGameScheduleList = () => {
    const url = this.props.url_game.GAME_SCHEDULE_LIST + '?keyword=&page=1&size=10&sort=id&status=1&order=DESC';
    this.props.ajaxUtil.sendRequest(url, {}, gameSchedule => {
      if (!gameSchedule.status) {
        this.props.setNotification({
          message: gameSchedule.message,
          hasError: !gameSchedule.status,
          timestamp: new Date().getTime()
        });
      }
      this.setState({ gameList: gameSchedule.data });
    }, this.props.loadingFunction,
      { method: 'GET', isShowSuccess: false });
  }

  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <div className="mainbg cus-pills">
              <ul className="nav nav-pills">
                <li className="nav-item">
                  <a className="nav-link active" href="">Gamification</a>
                </li>
              </ul>
            </div>
            <div className="Sub__tab__header">
              <div className="sub__header__left">
                <h6>All Entries | {this.state.gameList.length}</h6>
                <button onClick={this.openModal} className="add__training__btn ml-3">
                  Add Game
                </button>
              </div>
              <div className="sub__header__right">
                <div className="searchbox">
                  <input
                    type="search"
                    placeholder="What are you looking for ?"
                  />
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-12">
            <div className="table-responsive">
              <table className="display dtable w-100">
                <thead>
                  <tr>
                    <th>S.No</th>
                    <th>Game Name</th>
                    <th>Date Range</th>
                    <th>Participants</th>
                    <th>Edit</th>
                    <th>Delete</th>
                  </tr>
                </thead>
                <tbody>
                  {
                    (this.state.gameList.length > 0) ?
                      this.state.gameList.map((game, index) => (
                        <tr>
                          <td>{index + 1}</td>
                          <td>{game.game_name}</td>
                          <td>{game.start_date} to {game.end_date}</td>
                          <td>
                            <div className="btn__participants">
                              <button className="btn">
                                &nbsp;
                                <img
                                  src="/dms/images/plus.svg"
                                  alt="add"
                                />  &nbsp;Participants
                              </button>
                            </div>
                          </td>
                          <td>
                            <button onClick={() => { }} className="btn btn-action">
                              <img src="/dms/images/edit.svg" alt="Edit" />
                            </button>
                          </td>
                          <td>
                            <button
                              onClick={() => { }}
                              className="btn btn-action">
                              <img src="/dms/images/delete.svg" alt="Delete" />
                            </button>
                          </td>
                        </tr>
                      )) : (
                        <tr>
                          <td colSpan="6">No Data</td>
                        </tr>
                      )
                  }
                </tbody>
              </table>
            </div>
          </div>
          <Popup
            type={POPUP_ALIGN.RIGHT}
            title={this.state.isEdit ? "Update Game" : "Add Game"}
            isOpen={this.state.showAddModel}
            close={this.closeModal}
            minWidth="50%"
            component={
              <AddOrEditGame
                closeModal={this.closeModal}
                url_game={this.props.url_game}
                ajaxUtil={this.props.ajaxUtil}
                loadingFunction={this.props.loadingFunction}
                setNotification={this.props.setNotification}
              />
            }

          />
        </div>
      </div>
    )
  }
}
