import React, { useState } from 'react'
import { useTable, useSortBy, useExpanded, usePagination } from 'react-table'
import { Link } from 'react-router-dom';
function Table({ columns: userColumns, data }) {
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,

        prepareRow,
        page,
        canPreviousPage,
        canNextPage,
        pageOptions,
        pageCount,
        gotoPage,
        nextPage,
        previousPage,
        setPageSize,
        state: {  pageIndex, pageSize },
    } = useTable(
        {
            columns: userColumns,
            data,
            initialState: { pageIndex: 0 },
        },
        useSortBy,
        usePagination // We can useExpanded to track the expanded state
        // for sub components too!
    )

    return (
        <div>
            <table {...getTableProps()}  className="module-grid fullWidthPopup">
                <thead>
                    {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                                    {column.render('Header')}
                                    <span>
                                        {column.isSorted ? (!column.isSortedDesc ? ' ðŸ”¼' : ' ðŸ”½') : ''}
                                    </span>
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {page.map((row, i) => {
                        prepareRow(row)
                        return (
                            // Use a React.Fragment here so the table markup is still valid
                            <React.Fragment {...row.getRowProps()}>
                                <tr>
                                    {row.cells.map(cell => {
                                        return (
                                            <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                        )
                                    })}
                                </tr>
                            </React.Fragment>
                        )
                    })}
                </tbody>
            </table>
            <br />
            <div className="col-md-12 pagination grid-pagination">
                <div className="col-md-4">
                    <div className="form-group">
                        <select className="form-control"
                            value={pageSize}
                            onChange={e => {
                                setPageSize(Number(e.target.value))
                            }}
                        >
                            {[10, 20, 30, 40, 50].map(pageSize => (
                                <option key={pageSize} value={pageSize}>
                                    {pageSize}
                                </option>
                            ))}
                        </select>
                        <span className='m-l-10'>Records Per Page</span>
                    </div>
                </div>
                <div className="col-md-3 form-group">
                    <div className="form-group">
                        <span>
                            Page{' '}
                        </span>
                        <input
                            className="form-control m-l-10 "
                            type="number"
                            defaultValue={pageIndex + 1}
                            onChange={e => {
                                const page = e.target.value ? Number(e.target.value) - 1 : 0
                                gotoPage(page)
                            }}
                        />
                        <span className="m-l-10">of {pageOptions.length}</span>
                    </div>
                </div>
                <div className="col-md-5">
                    <ul>
                        <li onClick={() => gotoPage(0)} disabled={!canPreviousPage}><a href="#">First</a></li>
                        <li onClick={() => previousPage()} disabled={!canPreviousPage}><a href="#">Prev</a></li>
                        <li onClick={() => gotoPage(pageIndex + 1)}><a href="#">{pageIndex + 1}</a></li>
                        <li onClick={() => gotoPage(pageIndex + 1)}><a href="#">{pageIndex + 2}</a></li>
                        <li onClick={() => nextPage()} disabled={!canNextPage}><a href="#">Next</a></li>
                        <li onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}><a href="#">Last</a></li>
                    </ul>

                </div>
            </div>
        </div>
    )
}


