import React, { useState } from 'react';

export default function RejectionModal(props) {
  const { closeAddRejectionMessageModal, saveRejectionMessages, selectedFeedback } = props;
  let initialValue = [''];
  if (selectedFeedback.hasOwnProperty('id')) {
    initialValue = [selectedFeedback.message]

  }
  const [messages, setRejectionMessages] = useState(initialValue);
  const updateValue = (event, index) => {
    let msg = [...messages];
    msg[index] = event.currentTarget.value;
    setRejectionMessages(msg);
  }
  const addMessageField = () => {
    let msg = [...messages];
    msg.push('');
    setRejectionMessages(msg);
  }
  const deleteField = (index) => {
    let msg = [...messages];
    if(msg.length > 1) {
      msg.splice(index, 1);
      setRejectionMessages(msg);
    }

  }
  return (
    <div className="container-fluid">
      <div className="row mt-2 d-flex flex-column justify-content-between">
        <div className="col-md-12">
          {selectedFeedback.hasOwnProperty('id') ?
            <UpdateFeedbackMessage updateValue={updateValue} messages={messages} /> :
            <AddMessages
              addMessageField={addMessageField}
              messages={messages}
              deleteField={deleteField}
              updateValue={updateValue} />}
        </div>
        <div className="col-md-12  text-right">

          <button
            onClick={closeAddRejectionMessageModal}
            type="button"
            className="btn btn-danger cancel ">Cancel</button>
          <button
            onClick={() => setRejectionMessages([''])}
            type="button"
            className="btn btn-danger reset ">Reset</button>
          <button
            onClick={() => saveRejectionMessages(messages)}
            type="button"
            className="btn btn-danger add ">{selectedFeedback.hasOwnProperty('id') ? 'Update' : 'Add'}</button>

        </div>
      </div>
    </div>
  )
}

function AddMessages(props) {
  const { messages, updateValue, addMessageField, deleteField } = props;
  return messages.map((element, index) => (
    <div className="form-group" key={index}>
      <label htmlFor="Training">Rejection Message {index + 1}</label>
      <div className="d-flex">
        <input type="text" className="form-control" value={element} onChange={(event) => updateValue(event, index)} placeholder="Rejection Message" />
        <div className="d-flex">
          <button className="btn" onClick={addMessageField}><img src="/dms/images/plus.svg" alt="add" /></button>
          <button className="btn" onClick={() => deleteField(index)}><img src="/dms/images/minus.svg" alt="remove" /></button>
        </div>
      </div>
    </div>
  ));
}

function UpdateFeedbackMessage(props) {
  const { messages, updateValue } = props;
  return (
    <div className="form-group" >
      <label htmlFor="Training">Rejection Message </label>
      <div className="d-flex">
        <input type="text" className="form-control" value={messages[0]} onChange={(event) => updateValue(event, 0)} placeholder="Rejection Message" />

      </div>
    </div>
  )
}
