const initialState = {
    currentSyllabusId:'',
    currentMaterialId:'',
    currentMaterialName:'',
    currentModuleId:'',
    currentModuleName:'',
    currentQuizId:'',
    currentParticipantId:'',
    // currentMenu:'module_menu',
}

export default function (state = initialState , action) {
    switch(action.type) {
        case 'syllabus' :
         return {...state, currentSyllabusId: action.payload};
        case 'module' :
         return {...state, currentModuleId: action.payload.id, currentModuleName:action.payload.moduleName};
        case 'material' :
         return {...state, currentMaterialId: action.payload.id, currentMaterialName:action.payload.materialName};
        case 'quiz' :
            return {...state, currentQuizId: action.payload};
        case 'participant' :
         return {...state, currentParticipantId: action.payload};
        // case 'menu' :
        //     return {...state, currentMenu: action.payload};
        default:
          return state;
    }
}
